import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [currentSem, setCurrentSem] = useState(7);
  const [subjects, setSubjects] = useState([]);
  const [loading, setLoading] = useState(true);

  // FETCH DATA FROM YOUR NODE.JS SERVER
  useEffect(() => {
    fetch('http://localhost:5000/api/subjects')
      .then(response => response.json())
      .then(data => {
        setSubjects(data);
        setLoading(false);
      })
      .catch(error => console.error("Error fetching data:", error));
  }, []);

  const filteredSubjects = subjects.filter(item => item.semester === currentSem);

  return (
    <div className="App" style={{ padding: "20px" }}>
      <h1>VTU Resource Hub (MERN Stack)</h1>

      {/* Semester Buttons */}
      <div style={{ marginBottom: '20px' }}>
        {[5, 6, 7, 8].map(sem => (
          <button 
            key={sem} 
            onClick={() => setCurrentSem(sem)}
            style={{ marginRight: '10px', padding: '10px', background: currentSem===sem?'blue':'gray', color:'white' }}
          >
            {sem}th Sem
          </button>
        ))}
      </div>

      {/* List */}
      {loading ? <p>Connecting to Backend...</p> : (
        filteredSubjects.length === 0 ? <p>No data found in Database.</p> : 
        filteredSubjects.map(sub => (
          <div key={sub._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px 0" }}>
            <h3>{sub.subject} ({sub.subjectCode})</h3>
            <a href={sub.link} target="_blank" rel="noreferrer">Download Notes</a>
          </div>
        ))
      )}
    </div>
  );
}

export default App;
