import React, { useState, useEffect } from 'react';
import Admin from './Admin';
import { motion } from 'framer-motion';
import Marquee from "react-fast-marquee";
import { 
  FaUniversity, FaWhatsapp, FaTelegram, FaBars, FaTimes, 
  FaLaptopCode, FaMicrochip, FaCogs, FaBuilding, FaFlask, 
  FaFileAlt, FaDownload, FaBell 
} from 'react-icons/fa';
import './App.css'; // This connects the styling

function App() {
  const [currentBranch, setCurrentBranch] = useState("ALL");
  const [subjects, setSubjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isAdmin, setIsAdmin] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  // --- DATA ---
  const updates = [
    "üì¢ Extension of last date for internship selection",
    "üìÖ Revised Time Table for 7th Sem Exams",
    "üìù Submission of Online Application for Revaluation",
    "üéì Tentative Academic Calendar 2025-26"
  ];

  useEffect(() => {
    fetch('http://localhost:5000/api/subjects')
      .then(res => res.json())
      .then(data => { setSubjects(data); setLoading(false); })
      .catch(err => console.error("Error:", err));
  }, []);

  const departments = [
    { id: "CSE", name: "CSE-ISE", icon: <FaLaptopCode />, color: "card-blue" },
    { id: "ECE", name: "Electronics", icon: <FaMicrochip />, color: "card-purple" },
    { id: "MECH", name: "Mechanical", icon: <FaCogs />, color: "card-orange" },
    { id: "CIVIL", name: "Civil Engg", icon: <FaBuilding />, color: "card-green" },
    { id: "FY", name: "First Year", icon: <FaFlask />, color: "card-pink" },
  ];

  const filteredSubjects = currentBranch === "ALL" ? subjects : subjects.filter(sub => sub.branch === currentBranch || sub.branch === "CSE");

  return (
    <div className="app-container">

      {/* --- NAVBAR --- */}
      <nav className="navbar">
        <div className="nav-content">
          <div className="logo-section" onClick={() => setCurrentBranch("ALL")}>
            <div className="logo-icon"><FaUniversity /></div>
            <h1>Mandeep's Guide</h1>
          </div>

          <div className={`nav-links ${mobileMenuOpen ? "mobile-open" : ""}`}>
            <span>Home</span>
            <span>Notes</span>
            <span>Syllabus</span>
            <span>Results</span>
            <button onClick={() => setIsAdmin(!isAdmin)} className="btn-admin">
              {isAdmin ? "Exit Admin" : "Admin Panel"}
            </button>
          </div>

          <div className="menu-toggle" onClick={() => setMobileMenuOpen(!mobileMenuOpen)}>
            {mobileMenuOpen ? <FaTimes /> : <FaBars />}
          </div>
        </div>
      </nav>

      {/* --- TICKER SECTION --- */}
      <div className="ticker-section">
        <span className="ticker-label">LIVE UPDATES</span>
        <Marquee gradient={false} speed={50} className="ticker-text">
          {updates.map((u, i) => <span key={i}>‚Ä¢ {u} &nbsp;&nbsp;&nbsp;&nbsp;</span>)}
        </Marquee>
      </div>

      {/* --- MAIN LAYOUT --- */}
      <div className="main-grid">

        {/* === LEFT CONTENT === */}
        <div className="content-column">
          {isAdmin ? (
             <Admin />
          ) : (
            <>
              {/* Department Cards */}
              <section>
                <h3 className="section-title">Browse by Department</h3>
                <div className="dept-grid">
                  {departments.map((dept) => (
                    <motion.div 
                      key={dept.id}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      onClick={() => setCurrentBranch(dept.id)}
                      className={`dept-card ${dept.color}`}
                    >
                      <div className="dept-icon">{dept.icon}</div>
                      <h4>{dept.name}</h4>
                      <p>View Resources ‚ûú</p>
                    </motion.div>
                  ))}
                </div>
              </section>

              {/* Recent Uploads */}
              <section className="uploads-section">
                <h3 className="section-title">Latest Materials</h3>
                <div className="uploads-list">
                  {loading ? <p>Loading...</p> : filteredSubjects.slice(0, 6).map((sub) => (
                    <motion.div 
                      key={sub._id}
                      initial={{ opacity: 0, x: -10 }}
                      whileInView={{ opacity: 1, x: 0 }}
                      className="upload-card"
                    >
                      <div className="upload-info">
                        <span className="code-badge">{sub.subjectCode}</span>
                        <div>
                          <h4>{sub.subject}</h4>
                          <small>{sub.branch} | {sub.semester}th Sem</small>
                        </div>
                      </div>
                      <a href={sub.link} target="_blank" rel="noreferrer" className="btn-download">
                        <FaDownload />
                      </a>
                    </motion.div>
                  ))}
                </div>
              </section>
            </>
          )}
        </div>

        {/* === RIGHT SIDEBAR === */}
        <div className="sidebar-column">

          {/* Social Widget */}
          <div className="sidebar-widget gradient-widget">
            <h3>Join Community</h3>
            <p>Get instant alerts on your phone.</p>
            <button className="btn-social whatsapp"><FaWhatsapp /> WhatsApp Group</button>
            <button className="btn-social telegram"><FaTelegram /> Telegram Channel</button>
          </div>

          {/* Tools Widget (SGPA REMOVED) */}
          <div className="sidebar-widget">
            <h3>Quick Tools</h3>
            <div className="tools-grid">
              <div className="tool-item">
                <FaFileAlt className="tool-icon" />
                <span>Model Papers</span>
              </div>
              <div className="tool-item">
                <FaBell className="tool-icon" />
                <span>Syllabus Copy</span>
              </div>
            </div>
          </div>

        </div>
      </div>

      {/* --- FOOTER --- */}
      <footer className="footer">
        <p>¬© 2025 Mandeep's Guide. Designed with ‚ù§Ô∏è in India.</p>
      </footer>
    </div>
  );
}

export default App;
