import React, { useState, useEffect } from 'react';
import Admin from './Admin';
import Marquee from "react-fast-marquee";
import { 
  FaUniversity, FaWhatsapp, FaTelegram, FaBars, FaTimes, 
  FaLaptopCode, FaMicrochip, FaCogs, FaBuilding, FaFlask, 
  FaFileAlt, FaDownload, FaTrophy, FaBookOpen, 
  FaArrowLeft, FaCalendarAlt, FaCommentDots, FaPaperPlane
} from 'react-icons/fa';
import { motion } from 'framer-motion';
import './App.css';

function App() {
  const [subjects, setSubjects] = useState([]);
  const [syllabusList, setSyllabusList] = useState([]); 
  const [loading, setLoading] = useState(true);

  // NAVIGATION STATE
  const [isAdmin, setIsAdmin] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activePage, setActivePage] = useState("home"); 

  // FILTER STATE (For Notes)
  const [currentBranch, setCurrentBranch] = useState("ALL");
  const [currentSemester, setCurrentSemester] = useState(null);

  // SYLLABUS STATE (For Dynamic Schemes)
  const [syllabusBranch, setSyllabusBranch] = useState(null);
  const [syllabusScheme, setSyllabusScheme] = useState(null);

  // FEEDBACK STATE
  const [feedback, setFeedback] = useState({ name: "", message: "" });
  const [feedbackStatus, setFeedbackStatus] = useState("");

  const updates = [
    { text: "ðŸ“¢ Extension of last date for internship selection", link: "https://vtu.ac.in/en/internships" },
    { text: "ðŸ“… Revised Time Table for 7th Sem Exams", link: "https://vtu.ac.in/en/exam-timetable" },
    { text: "ðŸ“ Submission of Online Application for Revaluation", link: "https://results.vtu.ac.in" },
    { text: "ðŸŽ“ Tentative Academic Calendar 2025-26", link: "https://vtu.ac.in/en/academic-calendar" }
  ];

  // FETCH DATA
  useEffect(() => {
    // 1. Fetch Notes
    fetch('http://localhost:5000/api/subjects')
      .then(res => res.json()).then(data => { setSubjects(data); setLoading(false); })
      .catch(err => console.error("Error Notes:", err));

    // 2. Fetch Syllabus
    fetch('http://localhost:5000/api/syllabus')
      .then(res => res.json()).then(data => setSyllabusList(data))
      .catch(err => console.error("Error Syllabus:", err));
  }, []);

  const departments = [
    { id: "CSE", name: "CSE-ISE", icon: <FaLaptopCode />, color: "card-blue" },
    { id: "ECE", name: "Electronics", icon: <FaMicrochip />, color: "card-purple" },
    { id: "MECH", name: "Mechanical", icon: <FaCogs />, color: "card-orange" },
    { id: "CIVIL", name: "Civil Engg", icon: <FaBuilding />, color: "card-green" },
    { id: "FY", name: "First Year", icon: <FaFlask />, color: "card-pink" },
  ];

  const handleNavClick = (page) => {
    setActivePage(page); setIsAdmin(false); setMobileMenuOpen(false);
    setCurrentBranch("ALL"); setCurrentSemester(null); setSyllabusBranch(null); setSyllabusScheme(null);
  };

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault(); setFeedbackStatus("Sending...");
    try {
      await fetch('http://localhost:5000/api/feedback', {
        method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(feedback)
      });
      setFeedbackStatus("âœ… Sent!"); setFeedback({ name: "", message: "" }); setTimeout(() => setFeedbackStatus(""), 3000);
    } catch (err) { setFeedbackStatus("âŒ Error."); }
  };

  // --- DYNAMIC SYLLABUS LOGIC ---

  // 1. Get List of Schemes available for the selected Branch from Database
  const availableSchemes = [...new Set(
    syllabusList
      .filter(item => item.branch === syllabusBranch) // Only get items for this branch
      .map(item => item.scheme) // Get the scheme names
  )].sort().reverse(); 

  // 2. Get the specific link for the selected scheme
  const currentSyllabusLink = syllabusList.find(
    s => s.branch === syllabusBranch && s.scheme === syllabusScheme
  )?.link;


  // --- PAGE COMPONENTS ---

  const SyllabusPage = () => (
    <div className="content-column">

      {/* STEP 1: SELECT DEPT */}
      {!syllabusBranch && (
        <section>
          <h3 className="section-title"><FaBookOpen /> Syllabus: Select Department</h3>
          <div className="dept-grid">
            {departments.map((dept) => (
              <motion.div key={dept.id} whileHover={{ scale: 1.05 }} onClick={() => setSyllabusBranch(dept.id)} className={`dept-card ${dept.color}`}>
                <div className="dept-icon">{dept.icon}</div><h4>{dept.name}</h4><p>Select Scheme âžœ</p>
              </motion.div>
            ))}
          </div>
        </section>
      )}

      {/* STEP 2: SELECT SCHEME (DYNAMIC) */}
      {syllabusBranch && !syllabusScheme && (
        <section>
          <button onClick={() => setSyllabusBranch(null)} style={{background:"none", border:"none", cursor:"pointer", fontWeight:"bold", marginBottom:"15px", display:"flex", alignItems:"center", gap:"5px", color:"#666"}}>
            <FaArrowLeft /> Back
          </button>
          <h3 className="section-title">Select Scheme for {syllabusBranch}</h3>

          {availableSchemes.length === 0 ? (
            <div className="empty-state">
              <h4>No Syllabus Found</h4>
              <p>We haven't uploaded any syllabus for {syllabusBranch} yet.</p>
              <button onClick={()=>setIsAdmin(true)} className="btn-small">Add One Now</button>
            </div>
          ) : (
            <div className="dept-grid">
              {availableSchemes.map((scheme) => (
                <motion.div key={scheme} whileHover={{ scale: 1.05 }} onClick={() => setSyllabusScheme(scheme)} className="dept-card" style={{borderLeft: "5px solid #2563eb", padding: "30px"}}>
                  <div className="dept-icon"><FaCalendarAlt /></div>
                  <h4>{scheme}</h4>
                  <p>View PDF âžœ</p>
                </motion.div>
              ))}
            </div>
          )}
        </section>
      )}

      {/* STEP 3: DOWNLOAD */}
      {syllabusBranch && syllabusScheme && (
        <section>
           <button onClick={() => setSyllabusScheme(null)} style={{background:"none", border:"none", cursor:"pointer", fontWeight:"bold", marginBottom:"15px", display:"flex", alignItems:"center", gap:"5px", color:"#666"}}>
            <FaArrowLeft /> Back
          </button>
          <h3 className="section-title">Download Syllabus</h3>
          <div className="upload-card" style={{border: "2px solid #2563eb", flexDirection: "column", alignItems: "flex-start", gap: "15px"}}>
            <div className="upload-info">
              <span className="code-badge" style={{background:"#2563eb", color:"white"}}>PDF</span>
              <div><h4 style={{fontSize: "1.2rem"}}>{syllabusBranch} - {syllabusScheme}</h4><small>Official Copy</small></div>
            </div>

            <button className="btn-download" style={{width: "100%", borderRadius: "8px", height: "50px", background: "#2563eb", color: "white", fontWeight: "bold"}} onClick={() => window.open(currentSyllabusLink, '_blank')}>
              <FaDownload /> Download PDF
            </button>
          </div>
        </section>
      )}
    </div>
  );

  const ResultsPage = () => (
    <div className="content-column">
      <h3 className="section-title"><FaTrophy /> Check VTU Results</h3>
      <div className="dept-grid">
        <div className="dept-card card-blue" onClick={() => window.open('https://results.vtu.ac.in', '_blank')}><div className="dept-icon"><FaLaptopCode /></div><h4>7th & 8th Sem</h4></div>
        <div className="dept-card card-green" onClick={() => window.open('https://results.vtu.ac.in', '_blank')}><div className="dept-icon"><FaFlask /></div><h4>1st & 2nd Sem</h4></div>
      </div>
    </div>
  );

  return (
    <div className="app-container">

      {/* NAVBAR */}
      <nav className="navbar">
        <div className="nav-content">
          <div className="logo-section" onClick={() => handleNavClick("home")}><div className="logo-icon"><FaUniversity /></div><h1>Mandeep's Guide</h1></div>
          <div className={`nav-links ${mobileMenuOpen ? "mobile-open" : ""}`}>
            <span onClick={() => handleNavClick("home")}>Home</span><span onClick={() => handleNavClick("syllabus")}>Syllabus</span><span onClick={() => handleNavClick("results")}>Results</span>
            <button onClick={() => setIsAdmin(!isAdmin)} className="btn-admin">{isAdmin ? "Exit" : "Admin"}</button>
          </div>
          <div className="menu-toggle" onClick={() => setMobileMenuOpen(!mobileMenuOpen)}>{mobileMenuOpen ? <FaTimes /> : <FaBars />}</div>
        </div>
      </nav>

      {/* TICKER */}
      <div className="ticker-section"><span className="ticker-label">LIVE</span><Marquee gradient={false} speed={50} className="ticker-text">{updates.map((u, i) => <a key={i} href={u.link} target="_blank" rel="noreferrer" style={{color:"white", marginRight:"40px"}}>â€¢ {u.text}</a>)}</Marquee></div>

      {/* MAIN GRID */}
      <div className="main-grid">

        {/* LEFT COLUMN */}
        {isAdmin ? <div className="content-column"><Admin /></div> : activePage === "syllabus" ? <SyllabusPage /> : activePage === "results" ? <ResultsPage /> : (
          <div className="content-column">

            {/* NOTES VIEW: Step 1 (Departments) */}
            {currentBranch === "ALL" && (
              <section>
                <h3 className="section-title">Departments</h3>
                <div className="dept-grid">{departments.map((dept) => (<motion.div key={dept.id} whileHover={{ scale: 1.05 }} onClick={() => { setCurrentBranch(dept.id); setCurrentSemester(null); }} className={`dept-card ${dept.color}`}><div className="dept-icon">{dept.icon}</div><h4>{dept.name}</h4></motion.div>))}</div>
                <section className="uploads-section"><h3 className="section-title">Latest</h3><div className="uploads-list">{subjects.slice(0,3).map(sub=>(<div key={sub._id} className="upload-card"><div className="upload-info"><h4>{sub.subject}</h4><small>{sub.branch}</small></div><a href={sub.link} target="_blank" rel="noreferrer" className="btn-download"><FaDownload /></a></div>))}</div></section>
              </section>
            )}

            {/* NOTES VIEW: Step 2 (Semesters) */}
            {currentBranch !== "ALL" && currentSemester === null && (
              <section>
                <button onClick={() => setCurrentBranch("ALL")} style={{marginBottom:"20px", background:"none", border:"none", cursor:"pointer", color:"#666", display:"flex", alignItems:"center", gap:"5px", fontWeight:"bold"}}>
                  <FaArrowLeft /> Back
                </button>
                <div className="dept-grid">{[1, 2, 3, 4, 5, 6, 7, 8].map((sem) => (<motion.div key={sem} whileHover={{ scale: 1.05 }} onClick={() => setCurrentSemester(sem)} className="dept-card"><h4>{sem}th Sem</h4></motion.div>))}</div>
              </section>
            )}

            {/* NOTES VIEW: Step 3 (Download List) */}
            {currentBranch !== "ALL" && currentSemester !== null && (
              <section className="uploads-section">
                <button onClick={() => setCurrentSemester(null)} style={{marginBottom:"20px", background:"none", border:"none", cursor:"pointer", color:"#666", display:"flex", alignItems:"center", gap:"5px", fontWeight:"bold"}}>
                  <FaArrowLeft /> Back
                </button>
                <h3 className="section-title">{currentBranch} - {currentSemester}th Sem</h3>
                <div className="uploads-list">{loading ? <p>Loading...</p> : subjects.filter(sub => sub.branch === currentBranch && sub.semester === currentSemester).length === 0 ? <p>No notes found.</p> : subjects.filter(sub => sub.branch === currentBranch && sub.semester === currentSemester).map((sub) => (<div key={sub._id} className="upload-card"><div className="upload-info"><h4>{sub.subject}</h4></div><a href={sub.link} target="_blank" rel="noreferrer" className="btn-download"><FaDownload /></a></div>))}</div>
              </section>
            )}
          </div>
        )}

        {/* RIGHT SIDEBAR */}
        <div className="sidebar-column">

          {/* PROFILE */}
          <div className="sidebar-widget profile-widget">
            <div className="profile-img-container">
              <img src={process.env.PUBLIC_URL + '/profile.jpg'} onError={(e) => {e.target.onerror = null; e.target.src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png"}} alt="Mandeep" />
            </div>
            <h3>Mandeep</h3><p className="profile-role">Software Engineer</p>
          </div>

          {/* SOCIALS */}
          <div className="sidebar-widget gradient-widget">
            <h3>Join Community</h3>
            <button className="btn-social whatsapp" onClick={() => window.open('https://chat.whatsapp.com/YOUR_LINK', '_blank')}><FaWhatsapp /> WhatsApp</button>
            <button className="btn-social telegram" onClick={() => window.open('https://t.me/YOUR_LINK', '_blank')}><FaTelegram /> Telegram</button>
          </div>

          {/* FEEDBACK */}
          <div className="sidebar-widget">
            <h3><FaCommentDots style={{color:"#2563eb", marginRight:"5px"}}/> Feedback</h3>
            <form onSubmit={handleFeedbackSubmit} style={{display:"flex", flexDirection:"column", gap:"10px"}}>
              <input placeholder="Name" value={feedback.name} onChange={e=>setFeedback({...feedback, name:e.target.value})} style={{padding:"8px", border:"1px solid #ddd", borderRadius:"5px"}}/>
              <textarea placeholder="Message" value={feedback.message} onChange={e=>setFeedback({...feedback, message:e.target.value})} style={{padding:"8px", border:"1px solid #ddd", borderRadius:"5px"}}/>
              <button type="submit" style={{background:"#2563eb", color:"white", padding:"8px", border:"none", borderRadius:"5px", cursor:"pointer", display:"flex", alignItems:"center", justifyContent:"center", gap:"5px"}}>
                <FaPaperPlane size={12}/> Send
              </button>
            </form>
            {feedbackStatus && <p style={{marginTop:"5px", fontSize:"0.8rem", color:"green"}}>{feedbackStatus}</p>}
          </div>

        </div>
      </div>
      <footer className="footer"><p>Â© 2025 Mandeep's Guide</p></footer>
    </div>
  );
}

export default App;
