import React, { useState, useEffect } from 'react';
import Admin from './Admin';
import { motion } from 'framer-motion';
import Marquee from "react-fast-marquee";
import { 
  FaUniversity, FaWhatsapp, FaTelegram, FaBars, FaTimes, 
  FaLaptopCode, FaMicrochip, FaCogs, FaBuilding, FaFlask, 
  FaFileAlt, FaDownload, FaBell, FaTrophy, FaBookOpen, FaArrowLeft, FaLayerGroup, FaPaperPlane, FaCommentDots
} from 'react-icons/fa';
import './App.css';

function App() {
  const [subjects, setSubjects] = useState([]);
  const [loading, setLoading] = useState(true);

  // STATE MANAGEMENT
  const [isAdmin, setIsAdmin] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activePage, setActivePage] = useState("home"); 

  // FILTER STATES
  const [currentBranch, setCurrentBranch] = useState("ALL");
  const [currentSemester, setCurrentSemester] = useState(null);

  // FEEDBACK STATE
  const [feedback, setFeedback] = useState({ name: "", message: "" });
  const [feedbackStatus, setFeedbackStatus] = useState("");

  // LIVE UPDATES
  const updates = [
    { text: "ðŸ“¢ Extension of last date for internship selection", link: "https://vtu.ac.in/en/internships" },
    { text: "ðŸ“… Revised Time Table for 7th Sem Exams", link: "https://vtu.ac.in/en/exam-timetable" },
    { text: "ðŸ“ Submission of Online Application for Revaluation", link: "https://results.vtu.ac.in" },
    { text: "ðŸŽ“ Tentative Academic Calendar 2025-26", link: "https://vtu.ac.in/en/academic-calendar" }
  ];

  // FETCH DATA
  useEffect(() => {
    fetch('http://localhost:5000/api/subjects')
      .then(res => res.json())
      .then(data => { setSubjects(data); setLoading(false); })
      .catch(err => console.error("Error:", err));
  }, []);

  const departments = [
    { id: "CSE", name: "CSE-ISE", icon: <FaLaptopCode />, color: "card-blue" },
    { id: "ECE", name: "Electronics", icon: <FaMicrochip />, color: "card-purple" },
    { id: "MECH", name: "Mechanical", icon: <FaCogs />, color: "card-orange" },
    { id: "CIVIL", name: "Civil Engg", icon: <FaBuilding />, color: "card-green" },
    { id: "FY", name: "First Year", icon: <FaFlask />, color: "card-pink" },
  ];

  // FILTER LOGIC
  const filteredSubjects = subjects.filter(sub => {
    if (currentBranch === "ALL") return true; 
    return sub.branch === currentBranch && sub.semester === currentSemester;
  });

  // NAVIGATION HANDLER
  const handleNavClick = (page) => {
    setActivePage(page);
    setIsAdmin(false); 
    setMobileMenuOpen(false);
    if (page === "home") {
      setCurrentBranch("ALL");
      setCurrentSemester(null);
    }
  };

  // FEEDBACK SUBMIT HANDLER
  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    setFeedbackStatus("Sending...");
    try {
      const res = await fetch('http://localhost:5000/api/feedback', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(feedback)
      });
      if (res.ok) {
        setFeedbackStatus("âœ… Sent! Thank you.");
        setFeedback({ name: "", message: "" });
        setTimeout(() => setFeedbackStatus(""), 3000);
      } else {
        setFeedbackStatus("âŒ Failed to send.");
      }
    } catch (err) { setFeedbackStatus("âŒ Error."); }
  };

  // --- SUB-PAGES ---
  const SyllabusPage = () => (
    <div className="content-column">
      <h3 className="section-title"><FaBookOpen /> VTU Syllabus Copies</h3>
      <div className="uploads-list">
        {['2022 Scheme', '2021 Scheme', '2018 Scheme'].map((scheme, i) => (
          <div key={i} className="upload-card">
            <div className="upload-info">
              <span className="code-badge">PDF</span>
              <div><h4>{scheme} Syllabus</h4><small>Official VTU Copy</small></div>
            </div>
            <button className="btn-download"><FaDownload /></button>
          </div>
        ))}
      </div>
    </div>
  );

  const ResultsPage = () => (
    <div className="content-column">
      <h3 className="section-title"><FaTrophy /> Check VTU Results</h3>
      <div className="dept-grid">
        <div className="dept-card card-blue" onClick={() => window.open('https://results.vtu.ac.in', '_blank')}>
          <div className="dept-icon"><FaLaptopCode /></div><h4>7th & 8th Sem</h4><p>Click to Check âžœ</p>
        </div>
        <div className="dept-card card-green" onClick={() => window.open('https://results.vtu.ac.in', '_blank')}>
          <div className="dept-icon"><FaFlask /></div><h4>1st & 2nd Sem</h4><p>Click to Check âžœ</p>
        </div>
      </div>
    </div>
  );

  return (
    <div className="app-container">

      {/* NAVBAR */}
      <nav className="navbar">
        <div className="nav-content">
          <div className="logo-section" onClick={() => handleNavClick("home")}>
            <div className="logo-icon"><FaUniversity /></div>
            <h1>Mandeep's Guide</h1>
          </div>
          <div className={`nav-links ${mobileMenuOpen ? "mobile-open" : ""}`}>
            <span onClick={() => handleNavClick("home")} className={activePage==="home" && !isAdmin ? "active-link" : ""}>Home</span>
            <span onClick={() => handleNavClick("home")} className={activePage==="home" && !isAdmin ? "active-link" : ""}>Notes</span>
            <span onClick={() => handleNavClick("syllabus")} className={activePage==="syllabus" ? "active-link" : ""}>Syllabus</span>
            <span onClick={() => handleNavClick("results")} className={activePage==="results" ? "active-link" : ""}>Results</span>
            <button onClick={() => setIsAdmin(!isAdmin)} className="btn-admin">{isAdmin ? "Exit Admin" : "Admin Panel"}</button>
          </div>
          <div className="menu-toggle" onClick={() => setMobileMenuOpen(!mobileMenuOpen)}>
            {mobileMenuOpen ? <FaTimes /> : <FaBars />}
          </div>
        </div>
      </nav>

      {/* TICKER */}
      <div className="ticker-section">
        <span className="ticker-label">LIVE UPDATES</span>
        <Marquee gradient={false} speed={50} className="ticker-text">
          {updates.map((u, i) => (
            <a key={i} href={u.link} target="_blank" rel="noreferrer" style={{ textDecoration: "none", color: "white", marginRight: "40px", cursor: "pointer" }}>â€¢ {u.text}</a>
          ))}
        </Marquee>
      </div>

      {/* MAIN CONTENT */}
      <div className="main-grid">

        {/* LEFT CONTENT COLUMN */}
        {isAdmin ? (
           <div className="content-column"><Admin /></div>
        ) : activePage === "syllabus" ? (
           <SyllabusPage />
        ) : activePage === "results" ? (
           <ResultsPage />
        ) : (
          <div className="content-column">

            {/* VIEW 1: HOME */}
            {currentBranch === "ALL" && (
              <>
                <section>
                  <h3 className="section-title">Browse by Department</h3>
                  <div className="dept-grid">
                    {departments.map((dept) => (
                      <motion.div 
                        key={dept.id} whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}
                        onClick={() => { setCurrentBranch(dept.id); setCurrentSemester(null); }}
                        className={`dept-card ${dept.color}`}
                      >
                        <div className="dept-icon">{dept.icon}</div>
                        <h4>{dept.name}</h4><p>Select Sem âžœ</p>
                      </motion.div>
                    ))}
                  </div>
                </section>
                <section className="uploads-section">
                  <h3 className="section-title">Recently Added</h3>
                  <div className="uploads-list">
                    {subjects.slice(0, 5).map(sub => (
                       <div key={sub._id} className="upload-card">
                         <div className="upload-info"><span className="code-badge">{sub.subjectCode}</span><div><h4>{sub.subject}</h4><small>{sub.branch} | {sub.semester}th Sem</small></div></div>
                         <a href={sub.link} target="_blank" rel="noreferrer" className="btn-download"><FaDownload /></a>
                       </div>
                    ))}
                  </div>
                </section>
              </>
            )}

            {/* VIEW 2: SEMESTER SELECTION */}
            {currentBranch !== "ALL" && currentSemester === null && (
              <section>
                <button onClick={() => setCurrentBranch("ALL")} style={{marginBottom:"20px", display:"flex", alignItems:"center", gap:"10px", background:"none", border:"none", cursor:"pointer", color:"#666", fontWeight:"bold"}}>
                  <FaArrowLeft /> Back to Departments
                </button>
                <h3 className="section-title">Select Semester for {currentBranch}</h3>
                <div className="dept-grid">
                  {[1, 2, 3, 4, 5, 6, 7, 8].map((sem) => (
                    <motion.div 
                      key={sem} whileHover={{ scale: 1.05 }}
                      onClick={() => setCurrentSemester(sem)}
                      className="dept-card"
                      style={{borderLeft: "5px solid #2563eb"}}
                    >
                      <div className="dept-icon"><FaLayerGroup /></div>
                      <h4>{sem}th Semester</h4><p>View Notes âžœ</p>
                    </motion.div>
                  ))}
                </div>
              </section>
            )}

            {/* VIEW 3: NOTES LIST */}
            {currentBranch !== "ALL" && currentSemester !== null && (
              <section className="uploads-section">
                <button onClick={() => setCurrentSemester(null)} style={{marginBottom:"20px", display:"flex", alignItems:"center", gap:"10px", background:"none", border:"none", cursor:"pointer", color:"#666", fontWeight:"bold"}}>
                  <FaArrowLeft /> Back to Semesters
                </button>
                <h3 className="section-title">{currentBranch} - {currentSemester}th Semester</h3>
                <div className="uploads-list">
                  {loading ? ( <p>ðŸ”„ Loading...</p> ) : filteredSubjects.length === 0 ? (
                    <div className="empty-state">
                      <h4>No notes found for {currentBranch} {currentSemester}th Sem</h4>
                      <p>Be the first to upload notes!</p>
                      <button onClick={() => setIsAdmin(true)} className="btn-small">Upload Now</button>
                    </div>
                  ) : (
                    filteredSubjects.map((sub) => (
                      <motion.div 
                        key={sub._id} initial={{ opacity: 0, x: -10 }} whileInView={{ opacity: 1, x: 0 }}
                        className="upload-card"
                      >
                        <div className="upload-info">
                          <span className="code-badge">{sub.subjectCode}</span>
                          <div><h4>{sub.subject}</h4><small>{sub.branch} | {sub.semester}th Sem</small></div>
                        </div>
                        <a href={sub.link} target="_blank" rel="noreferrer" className="btn-download"><FaDownload /></a>
                      </motion.div>
                    ))
                  )}
                </div>
              </section>
            )}
          </div>
        )}

        {/* SIDEBAR */}
        <div className="sidebar-column">

          {/* PROFILE WIDGET */}
          <div className="sidebar-widget profile-widget">
            <div className="profile-img-container">
               <img src={process.env.PUBLIC_URL + '/profile.jpg'} onError={(e) => {e.target.onerror = null; e.target.src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png"}} alt="Author" />
            </div>
            <h3>Mandeep</h3><p className="profile-role">Software Engineer</p>
            <p className="profile-bio">Welcome! I help engineering students with curated notes and updates.</p>
          </div>

          {/* SOCIALS */}
          <div className="sidebar-widget gradient-widget">
            <h3>Join Community</h3><p>Get instant alerts on your phone.</p>
            <button className="btn-social whatsapp" onClick={() => window.open('https://chat.whatsapp.com/YOUR_LINK', '_blank')}><FaWhatsapp /> WhatsApp Group</button>
            <button className="btn-social telegram" onClick={() => window.open('https://t.me/YOUR_LINK', '_blank')}><FaTelegram /> Telegram Channel</button>
          </div>

          {/* --- NEW: FEEDBACK WIDGET --- */}
          <div className="sidebar-widget">
            <h3 style={{display: "flex", alignItems: "center", gap: "10px", marginBottom: "15px"}}>
              <FaCommentDots style={{color: "#2563eb"}}/> Feedback
            </h3>
            <p style={{fontSize: "0.85rem", color: "#666", marginBottom: "10px"}}>Found a bug? Request a feature!</p>

            <form onSubmit={handleFeedbackSubmit} style={{display: "flex", flexDirection: "column", gap: "10px"}}>
              <input 
                type="text" placeholder="Your Name (Optional)" 
                value={feedback.name} onChange={e => setFeedback({...feedback, name: e.target.value})}
                style={{padding: "10px", borderRadius: "5px", border: "1px solid #ddd", fontSize: "0.9rem"}}
              />
              <textarea 
                placeholder="Write your message..." 
                value={feedback.message} onChange={e => setFeedback({...feedback, message: e.target.value})}
                required
