import React, { useState, useEffect } from 'react';
import { 
  FaUserShield, FaPlus, FaSignOutAlt, FaLock, 
  FaCommentDots, FaTrash, FaDatabase, FaFileUpload 
} from 'react-icons/fa';

function Admin() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState("");
  
  // Login State
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loginError, setLoginError] = useState("");
  
  // Data States
  const [feedbacks, setFeedbacks] = useState([]); 
  const [allNotes, setAllNotes] = useState([]); // Store all uploaded notes here
  
  // View State (tabs)
  const [activeTab, setActiveTab] = useState("upload"); // 'upload', 'manage', 'syllabus'

  // Forms
  const [noteData, setNoteData] = useState({ branch: 'CSE', semester: 7, subject: '', subjectCode: '', link: '' });
  const [syllabusData, setSyllabusData] = useState({ branch: 'CSE', scheme: '2022 Scheme', link: '' });
  const [newAdmin, setNewAdmin] = useState({ newUsername: "", newPassword: "" });
  const [message, setMessage] = useState("");

  // --- 1. LOGIN ---
  const handleLogin = async (e) => {
    e.preventDefault();
    setLoginError("");
    try {
      const res = await fetch('http://localhost:5000/api/login', {
        method: 'POST', headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password })
      });
      const data = await res.json();
      if (data.success) {
        setIsLoggedIn(true); setCurrentUser(data.username); 
        fetchFeedback(); 
        fetchNotes(); // Fetch notes when logging in
      } else { setLoginError("‚ùå " + data.message); }
    } catch (err) { setLoginError("‚ùå Server Error"); }
  };

  // --- 2. FETCH DATA ---
  const fetchFeedback = async () => {
    try { const res = await fetch('http://localhost:5000/api/feedback'); setFeedbacks(await res.json()); } catch (err) {}
  };

  const fetchNotes = async () => {
    try { const res = await fetch('http://localhost:5000/api/subjects'); setAllNotes(await res.json()); } catch (err) {}
  };

  // --- 3. DELETE FUNCTIONS ---
  const handleDeleteFeedback = async (id) => {
    if (!window.confirm("Delete this message?")) return;
    await fetch(`http://localhost:5000/api/feedback/${id}`, { method: 'DELETE' });
    setFeedbacks(feedbacks.filter(msg => msg._id !== id));
  };

  const handleDeleteNote = async (id) => {
    if (!window.confirm("‚ö†Ô∏è Are you sure you want to delete this file? Users won't be able to see it anymore.")) return;
    
    const res = await fetch(`http://localhost:5000/api/subjects/${id}`, { method: 'DELETE' });
    const data = await res.json();
    
    if (data.success) {
      // Remove from list immediately
      setAllNotes(allNotes.filter(note => note._id !== id));
      alert("‚úÖ Deleted Successfully");
    } else {
      alert("‚ùå Failed to delete");
    }
  };

  // --- 4. UPLOAD HANDLERS ---
  const handleNoteUpload = async (e) => {
    e.preventDefault(); setMessage("Uploading Note...");
    const res = await fetch('http://localhost:5000/api/subjects', {
      method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(noteData)
    });
    if (res.ok) { 
      setMessage("‚úÖ Note Uploaded!"); 
      setNoteData({ ...noteData, subject: '', subjectCode: '', link: '' }); 
      fetchNotes(); // Refresh list
    }
    else setMessage("‚ùå Failed");
  };

  const handleSyllabusUpload = async (e) => {
    e.preventDefault(); setMessage("Uploading Syllabus...");
    const res = await fetch('http://localhost:5000/api/syllabus', {
      method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(syllabusData)
    });
    if (res.ok) { setMessage("‚úÖ Syllabus Uploaded!"); setSyllabusData({ ...syllabusData, link: '' }); }
    else setMessage("‚ùå Failed");
  };

  const handleAddAdmin = async (e) => {
    e.preventDefault();
    const res = await fetch('http://localhost:5000/api/add-admin', {
      method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(newAdmin)
    });
    const data = await res.json();
    if (data.success) { alert("‚úÖ Admin Added!"); setNewAdmin({ newUsername: "", newPassword: "" }); }
    else alert("‚ùå " + data.message);
  };

  if (!isLoggedIn) {
    return (
      <div style={{maxWidth: "400px", margin: "50px auto", padding: "40px", background: "white", borderRadius: "15px", textAlign: "center", border: "1px solid #e5e7eb"}}>
        <h2 style={{marginBottom: "20px"}}>Admin Access</h2>
        <form onSubmit={handleLogin} style={{display: "flex", flexDirection: "column", gap: "15px"}}>
          <input type="text" placeholder="Username" value={username} onChange={e=>setUsername(e.target.value)} style={{padding: "10px"}} />
          <input type="password" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)} style={{padding: "10px"}} />
          <button type="submit" style={{padding: "10px", background: "#2563eb", color: "white", border: "none"}}>Login</button>
        </form>
        {loginError && <p style={{color: "red", marginTop: "10px"}}>{loginError}</p>}
      </div>
    );
  }

  return (
    <div style={{maxWidth: "900px", margin: "0 auto", padding: "20px"}}>
      <div style={{display: "flex", justifyContent: "space-between", marginBottom: "30px"}}>
        <h3>üëã {currentUser}</h3>
        <button onClick={() => setIsLoggedIn(false)} style={{background: "#fee2e2", color: "#dc2626", border: "none", padding: "5px 15px", borderRadius: "5px"}}>Logout</button>
      </div>

      {/* TABS */}
      <div style={{display: "flex", gap: "10px", marginBottom: "20px"}}>
        <button onClick={() => setActiveTab("upload")} style={{padding: "10px 20px", background: activeTab==="upload"?"#2563eb":"white", color: activeTab==="upload"?"white":"black", border: "1px solid #ddd", borderRadius: "5px", cursor: "pointer", display: "flex", alignItems: "center", gap: "5px"}}>
          <FaFileUpload /> Upload
        </button>
        <button onClick={() => setActiveTab("manage")} style={{padding: "10px 20px", background: activeTab==="manage"?"#dc2626":"white", color: activeTab==="manage"?"white":"black", border: "1px solid #ddd", borderRadius: "5px", cursor: "pointer", display: "flex", alignItems: "center", gap: "5px"}}>
          <FaDatabase /> Manage Files
        </button>
        <button onClick={() => setActiveTab("syllabus")} style={{padding: "10px 20px", background: activeTab==="syllabus"?"#9333ea":"white", color: activeTab==="syllabus"?"white":"black", border: "1px solid #ddd", borderRadius: "5px", cursor: "pointer", display: "flex", alignItems: "center", gap: "5px"}}>
          <FaDatabase /> Syllabus
        </button>
      </div>

      {/* --- TAB 1: UPLOAD NOTES --- */}
      {activeTab === "upload" && (
        <div style={{background: "white", padding: "30px", borderRadius: "15px", border: "1px solid #e5e7eb", marginBottom: "30px"}}>
          <form onSubmit={handleNoteUpload} style={{display: "flex", flexDirection: "column", gap: "15px"}}>
            <h3 style={{color: "#2563eb"}}>üìÑ Upload Notes</h3>
            <div style={{display: "grid", gridTemplateColumns: "1fr 1fr", gap: "10px"}}>
              <select value={noteData.branch} onChange={e=>setNoteData({...noteData, branch:e.target.value})} style={{padding: "10px"}}><option value="CSE">CSE</option><option value="ECE">ECE</option><option value="MECH">MECH</option><option value="CIVIL">CIVIL</option><option value="FY">First Year</option></select>
              <select value={noteData.semester} onChange={e=>setNoteData({...noteData, semester:e.target.value})} style={{padding: "10px"}}>{[1,2,3,4,5,6,7,8].map(s=><option key={s} value={s}>{s}th Sem</option>)}</select>
            </div>
            <input type="text" placeholder="Subject Name" value={noteData.subject} onChange={e=>setNoteData({...noteData, subject:e.target.value})} required style={{padding: "10px"}} />
            <input type="text" placeholder="Subject Code" value={noteData.subjectCode} onChange={e=>setNoteData({...noteData, subjectCode:e.target.value})} required style={{padding: "10px"}} />
            <input type="url" placeholder="PDF Link" value={noteData.link} onChange={e=>setNoteData({...noteData, link:e.target.value})} required style={{padding: "10px"}} />
            <button type="submit" style={{padding: "10px", background: "#16a34a", color: "white", border: "none", borderRadius: "5px"}}>Upload Note</button>
          </form>
          {message && <p style={{marginTop: "15px", fontWeight: "bold"}}>{message}</p>}
        </div>
      )}

      {/* --- TAB 2: MANAGE FILES (DELETE) --- */}
      {activeTab === "manage" && (
        <div style={{background: "white", padding: "20px", borderRadius: "15px", border: "1px solid #e5e7eb"}}>
          <h3 style={{marginBottom: "20px", color: "#dc2626"}}>üóë Manage Database</h3>
          <p style={{marginBottom: "15px", fontSize: "0.9rem", color: "#666"}}>Here you can delete incorrect uploads.</p>
          
          <div style={{maxHeight: "500px", overflowY: "auto"}}>
            {allNotes.length === 0 ? <p>No files found.</p> : allNotes.map((note) => (
              <div key={note._id} style={{display: "flex", justifyContent: "space-between", alignItems: "center", padding: "10px", borderBottom: "1px solid #eee"}}>
                <div>
                  <strong>{note.subject}</strong> <span style={{fontSize: "0.8rem", color: "#888"}}>({note.subjectCode})</span>
                  <div style={{fontSize: "0.85rem", color: "#555"}}>{note.branch} | {note.semester}th Sem</div>
                </div>
                <button onClick={() => handleDeleteNote(note._id)} style={{background: "#fee2e2", color: "red", border: "none", padding: "8px 12px", borderRadius: "5px", cursor: "pointer"}}>
                  <FaTrash /> Delete
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* --- TAB 3: UPLOAD SYLLABUS --- */}
      {activeTab === "syllabus" && (
        <div style={{background: "white", padding: "30px", borderRadius: "15px", border: "1px solid #e5e7eb"}}>
          <form onSubmit={handleSyllabusUpload} style={{display: "flex", flexDirection: "column", gap: "15px"}}>
            <h3 style={{color: "#9333ea"}}>üìò Upload Syllabus</h3>
            <div style={{display: "grid", gridTemplateColumns: "1fr 1fr", gap: "10px"}}>
              <select value={syllabusData.branch} onChange={e=>setSyllabusData({...syllabusData, branch:e.target.value})} style={{padding: "10px"}}><option value="CSE">CSE</option><option value="ECE">ECE</option><option value="MECH">MECH</option><option value="CIVIL">CIVIL</option><option value="FY">First Year</option></select>
              <input type="text" placeholder="Scheme (e.g. 2024 Scheme)" value={syllabusData.scheme} onChange={e=>setSyllabusData({...syllabusData, scheme:e.target.value})} required style={{padding: "10px", border: "1px solid #ddd", borderRadius: "5px"}} />
            </div>
            <input type="url" placeholder="Syllabus PDF Link" value={syllabusData.link} onChange={e=>setSyllabusData({...syllabusData, link:e.target.value})} required style={{padding: "10px"}} />
            <button type="submit" style={{padding: "10px", background: "#9333ea", color: "white", border: "none", borderRadius: "5px"}}>Upload Syllabus</button>
          </form>
          {message && <p style={{marginTop: "15px", fontWeight: "bold"}}>{message}</p>}
        </div>
      )}

      {/* FEEDBACK INBOX */}
      <div style={{marginTop: "40px", background: "#fdf2f8", padding: "20px", borderRadius: "10px", border: "1px solid #fbcfe8"}}>
        <h3 style={{color: "#db2777", marginBottom: "10px"}}><FaCommentDots /> Feedback Inbox</h3>
        {feedbacks.map((msg, i) => (
          <div key={i} style={{background: "white", padding: "10px", marginBottom: "5px", borderRadius: "5px", display: "flex", justifyContent: "space-between"}}>
            <span><strong>{msg.name}:</strong> {msg.message}</span>
            <FaTrash onClick={() => handleDeleteFeedback(msg._id)} style={{cursor: "pointer", color: "red"}}/>
          </div>
        ))}
      </div>

    </div>
  );
}
export default Admin;
