import React, { useState } from 'react';

function Admin() {
  const [formData, setFormData] = useState({
    branch: 'CSE',
    semester: 7,
    subject: '',
    subjectCode: '',
    link: ''
  });

  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("Uploading...");

    try {
      // Connect to your Backend
      const response = await fetch('http://localhost:5000/api/subjects', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        setMessage("‚úÖ Subject Added Successfully!");
        setFormData({ ...formData, subject: '', subjectCode: '', link: '' }); // Clear form
      } else {
        setMessage("‚ùå Error adding subject.");
      }
    } catch (error) {
      console.error("Error:", error);
      setMessage("‚ùå Server Error. Is Backend running?");
    }
  };

  return (
    <div style={{ maxWidth: "500px", margin: "20px auto", padding: "20px", border: "1px solid #ddd", borderRadius: "10px" }}>
      <h2>Admin Upload Panel</h2>
      
      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
        
        <label>
          Branch:
          <select name="branch" value={formData.branch} onChange={handleChange} style={{ width: "100%", padding: "8px" }}>
            <option value="CSE">CSE</option>
            <option value="ISE">ISE</option>
            <option value="ECE">ECE</option>
          </select>
        </label>

        <label>
          Semester:
          <select name="semester" value={formData.semester} onChange={handleChange} style={{ width: "100%", padding: "8px" }}>
            {[3,4,5,6,7,8].map(s => <option key={s} value={s}>{s}</option>)}
          </select>
        </label>

        <input 
          type="text" name="subject" placeholder="Subject Name (e.g., Cloud Computing)" 
          value={formData.subject} onChange={handleChange} required 
          style={{ padding: "10px" }}
        />

        <input 
          type="text" name="subjectCode" placeholder="Subject Code (e.g., 18CS71)" 
          value={formData.subjectCode} onChange={handleChange} required 
          style={{ padding: "10px" }}
        />

        <input 
          type="url" name="link" placeholder="Paste Google Drive/PDF Link here" 
          value={formData.link} onChange={handleChange} required 
          style={{ padding: "10px" }}
        />

        <button type="submit" style={{ padding: "10px", backgroundColor: "#28a745", color: "white", border: "none", cursor: "pointer", fontSize: "16px" }}>
          üöÄ Upload Resource
        </button>
      
      </form>

      {message && <p style={{ marginTop: "15px", fontWeight: "bold" }}>{message}</p>}
    </div>
  );
}

export default Admin;
