import React, { useState, useEffect } from 'react';
import Admin from './Admin';
import { motion } from 'framer-motion';
import Marquee from "react-fast-marquee";
import { 
  FaUniversity, FaWhatsapp, FaTelegram, FaBars, FaTimes, 
  FaLaptopCode, FaMicrochip, FaCogs, FaBuilding, FaFlask, 
  FaFileAlt, FaDownload, FaBell, FaTrophy, FaBookOpen
} from 'react-icons/fa';
import './App.css';

function App() {
  const [currentBranch, setCurrentBranch] = useState("ALL");
  const [subjects, setSubjects] = useState([]);
  const [loading, setLoading] = useState(true);

  // STATE MANAGEMENT
  const [isAdmin, setIsAdmin] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activePage, setActivePage] = useState("home"); 

  // FAKE NEWS UPDATES
  const updates = [
    "üì¢ Extension of last date for internship selection",
    "üìÖ Revised Time Table for 7th Sem Exams",
    "üìù Submission of Online Application for Revaluation",
    "üéì Tentative Academic Calendar 2025-26"
  ];

  // FETCH DATA
  useEffect(() => {
    fetch('http://localhost:5000/api/subjects')
      .then(res => res.json())
      .then(data => { setSubjects(data); setLoading(false); })
      .catch(err => console.error("Error:", err));
  }, []);

  const departments = [
    { id: "CSE", name: "CSE-ISE", icon: <FaLaptopCode />, color: "card-blue" },
    { id: "ECE", name: "Electronics", icon: <FaMicrochip />, color: "card-purple" },
    { id: "MECH", name: "Mechanical", icon: <FaCogs />, color: "card-orange" },
    { id: "CIVIL", name: "Civil Engg", icon: <FaBuilding />, color: "card-green" },
    { id: "FY", name: "First Year", icon: <FaFlask />, color: "card-pink" },
  ];

  const filteredSubjects = currentBranch === "ALL" ? subjects : subjects.filter(sub => sub.branch === currentBranch);

  // --- NAVIGATION HANDLER (Crucial Fix) ---
  const handleNavClick = (page) => {
    setActivePage(page);
    setIsAdmin(false); // <--- THIS LINE FIXES THE PROBLEM (Exits Admin Mode)
    setMobileMenuOpen(false);
  };

  // --- SUB-PAGES ---
  const SyllabusPage = () => (
    <div className="content-column">
      <h3 className="section-title"><FaBookOpen /> VTU Syllabus Copies</h3>
      <div className="uploads-list">
        {['2022 Scheme', '2021 Scheme', '2018 Scheme'].map((scheme, i) => (
          <div key={i} className="upload-card">
            <div className="upload-info">
              <span className="code-badge">PDF</span>
              <div>
                <h4>{scheme} Syllabus - All Branches</h4>
                <small>Official VTU Copy</small>
              </div>
            </div>
            <button className="btn-download"><FaDownload /></button>
          </div>
        ))}
      </div>
    </div>
  );

  const ResultsPage = () => (
    <div className="content-column">
      <h3 className="section-title"><FaTrophy /> Check VTU Results</h3>
      <div className="dept-grid">
        <div className="dept-card card-blue" onClick={() => window.open('https://results.vtu.ac.in', '_blank')}>
          <div className="dept-icon"><FaLaptopCode /></div>
          <h4>7th & 8th Sem</h4>
          <p>Click to Check ‚ûú</p>
        </div>
        <div className="dept-card card-green" onClick={() => window.open('https://results.vtu.ac.in', '_blank')}>
          <div className="dept-icon"><FaFlask /></div>
          <h4>1st & 2nd Sem</h4>
          <p>Click to Check ‚ûú</p>
        </div>
      </div>
      <p style={{marginTop: '20px', color: '#666', fontSize: '0.9rem'}}>*Links redirect to official VTU portal.</p>
    </div>
  );

  return (
    <div className="app-container">

      {/* --- NAVBAR --- */}
      <nav className="navbar">
        <div className="nav-content">
          <div className="logo-section" onClick={() => handleNavClick("home")}>
            <div className="logo-icon"><FaUniversity /></div>
            <h1>Mandeep's Guide</h1>
          </div>

          <div className={`nav-links ${mobileMenuOpen ? "mobile-open" : ""}`}>
            <span onClick={() => handleNavClick("home")} className={activePage==="home" && !isAdmin ? "active-link" : ""}>Home</span>
            <span onClick={() => handleNavClick("home")} className={activePage==="home" && !isAdmin ? "active-link" : ""}>Notes</span>
            <span onClick={() => handleNavClick("syllabus")} className={activePage==="syllabus" ? "active-link" : ""}>Syllabus</span>
            <span onClick={() => handleNavClick("results")} className={activePage==="results" ? "active-link" : ""}>Results</span>

            <button onClick={() => setIsAdmin(!isAdmin)} className="btn-admin">
              {isAdmin ? "Exit Admin" : "Admin Panel"}
            </button>
          </div>

          <div className="menu-toggle" onClick={() => setMobileMenuOpen(!mobileMenuOpen)}>
            {mobileMenuOpen ? <FaTimes /> : <FaBars />}
          </div>
        </div>
      </nav>

      {/* --- TICKER --- */}
      <div className="ticker-section">
        <span className="ticker-label">LIVE UPDATES</span>
        <Marquee gradient={false} speed={50} className="ticker-text">
          {updates.map((u, i) => <span key={i}>‚Ä¢ {u} &nbsp;&nbsp;&nbsp;&nbsp;</span>)}
        </Marquee>
      </div>

      {/* --- MAIN CONTENT AREA --- */}
      <div className="main-grid">

        {/* LEFT COLUMN */}
        {isAdmin ? (
           <div className="content-column"><Admin /></div>
        ) : activePage === "syllabus" ? (
           <SyllabusPage />
        ) : activePage === "results" ? (
           <ResultsPage />
        ) : (
          /* HOME VIEW */
          <div className="content-column">

            {/* Department Filter */}
            <section>
              <h3 className="section-title">Browse by Department</h3>
              <div className="dept-grid">
                {departments.map((dept) => (
                  <motion.div 
                    key={dept.id}
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={() => setCurrentBranch(dept.id)}
                    className={`dept-card ${dept.color}`}
                  >
                    <div className="dept-icon">{dept.icon}</div>
                    <h4>{dept.name}</h4>
                    <p>View Resources ‚ûú</p>
                  </motion.div>
                ))}
              </div>
            </section>

            {/* Notes List */}
            <section className="uploads-section">
              <h3 className="section-title">
                {currentBranch === "ALL" ? "Latest Materials" : `${currentBranch} Materials`}
              </h3>

              <div className="uploads-list">
                {loading ? (
                  <p style={{textAlign: "center", padding: "20px", color: "#666"}}>üîÑ Connecting to database...</p>
                ) : filteredSubjects.length === 0 ? (
                  <div className="empty-state">
                    <h4>No notes found for {currentBranch}</h4>
                    <p>Be the first to upload notes for this branch in the Admin Panel!</p>
                    <button onClick={() => setIsAdmin(true)} className="btn-small">Go to Admin</button>
                  </div>
                ) : (
                  filteredSubjects.map((sub) => (
                    <motion.div 
                      key={sub._id}
                      initial={{ opacity: 0, x: -10 }}
                      whileInView={{ opacity: 1, x: 0 }}
                      className="upload-card"
                    >
                      <div className="upload-info">
                        <span className="code-badge">{sub.subjectCode}</span>
                        <div>
                          <h4>{sub.subject}</h4>
                          <small>{sub.branch} | {sub.semester}th Sem</small>
                        </div>
                      </div>
                      <a href={sub.link} target="_blank" rel="noreferrer" className="btn-download">
                        <FaDownload />
                      </a>
                    </motion.div>
                  ))
                )}
              </div>
            </section>
          </div>
        )}

        {/* RIGHT SIDEBAR (Updated to fix Quick Tools) */}
        <div className="sidebar-column">
          <div className="sidebar-widget gradient-widget">
            <h3>Join Community</h3>
            <p>Get instant alerts on your phone.</p>
            <button className="btn-social whatsapp"><FaWhatsapp /> WhatsApp Group</button>
            <button className="btn-social telegram"><FaTelegram /> Telegram Channel</button>
          </div>

          <div className="sidebar-widget">
            <h3>Quick Tools</h3>
            <div className="tools-grid">
              <div className="tool-item" onClick={() => handleNavClick("results")}>
                <FaFileAlt className="tool-icon" />
                <span>Results</span>
              </div>
              <div className="tool-item" onClick={() => handleNavClick("syllabus")}>
                <FaBell className="tool-icon" />
                <span>Syllabus</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      <footer className="footer">
        <p>¬© 2025 Mandeep's Guide. Designed with ‚ù§Ô∏è in India.</p>
      </footer>
    </div>
  );
}

export default App;
