import React, { useState, useEffect } from 'react';
import Admin from './Admin';
import { motion } from 'framer-motion';
import Marquee from "react-fast-marquee";
import { 
  FaUniversity, FaWhatsapp, FaTelegram, FaBars, FaTimes, 
  FaLaptopCode, FaMicrochip, FaCogs, FaBuilding, FaFlask, 
  FaFileAlt, FaDownload, FaBell, FaTrophy, FaBookOpen, 
  FaArrowLeft, FaLayerGroup, FaPaperPlane, FaCommentDots, FaCalendarAlt
} from 'react-icons/fa';
import './App.css';

function App() {
  const [subjects, setSubjects] = useState([]);
  const [loading, setLoading] = useState(true);

  // STATE MANAGEMENT
  const [isAdmin, setIsAdmin] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activePage, setActivePage] = useState("home"); 

  // FILTER STATES (For Notes)
  const [currentBranch, setCurrentBranch] = useState("ALL");
  const [currentSemester, setCurrentSemester] = useState(null);

  // SYLLABUS STATES (New!)
  const [syllabusBranch, setSyllabusBranch] = useState(null);
  const [syllabusScheme, setSyllabusScheme] = useState(null);

  // FEEDBACK STATE
  const [feedback, setFeedback] = useState({ name: "", message: "" });
  const [feedbackStatus, setFeedbackStatus] = useState("");

  // LIVE UPDATES
  const updates = [
    { text: "ðŸ“¢ Extension of last date for internship selection", link: "https://vtu.ac.in/en/internships" },
    { text: "ðŸ“… Revised Time Table for 7th Sem Exams", link: "https://vtu.ac.in/en/exam-timetable" },
    { text: "ðŸ“ Submission of Online Application for Revaluation", link: "https://results.vtu.ac.in" },
    { text: "ðŸŽ“ Tentative Academic Calendar 2025-26", link: "https://vtu.ac.in/en/academic-calendar" }
  ];

  // FETCH DATA
  useEffect(() => {
    fetch('http://localhost:5000/api/subjects')
      .then(res => res.json())
      .then(data => { setSubjects(data); setLoading(false); })
      .catch(err => console.error("Error:", err));
  }, []);

  const departments = [
    { id: "CSE", name: "CSE-ISE", icon: <FaLaptopCode />, color: "card-blue" },
    { id: "ECE", name: "Electronics", icon: <FaMicrochip />, color: "card-purple" },
    { id: "MECH", name: "Mechanical", icon: <FaCogs />, color: "card-orange" },
    { id: "CIVIL", name: "Civil Engg", icon: <FaBuilding />, color: "card-green" },
    { id: "FY", name: "First Year", icon: <FaFlask />, color: "card-pink" },
  ];

  const schemes = ["2022 Scheme", "2021 Scheme", "2018 Scheme", "2017 Scheme", "2015 Scheme"];

  // FILTER LOGIC
  const filteredSubjects = subjects.filter(sub => {
    if (currentBranch === "ALL") return true; 
    return sub.branch === currentBranch && sub.semester === currentSemester;
  });

  // NAVIGATION HANDLER
  const handleNavClick = (page) => {
    setActivePage(page);
    setIsAdmin(false); 
    setMobileMenuOpen(false);

    // Reset all filters when changing pages
    setCurrentBranch("ALL");
    setCurrentSemester(null);
    setSyllabusBranch(null);
    setSyllabusScheme(null);
  };

  // FEEDBACK SUBMIT
  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    setFeedbackStatus("Sending...");
    try {
      const res = await fetch('http://localhost:5000/api/feedback', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(feedback)
      });
      if (res.ok) {
        setFeedbackStatus("âœ… Sent! Thank you.");
        setFeedback({ name: "", message: "" });
        setTimeout(() => setFeedbackStatus(""), 3000);
      } else { setFeedbackStatus("âŒ Failed."); }
    } catch (err) { setFeedbackStatus("âŒ Error."); }
  };

  // --- NEW: DYNAMIC SYLLABUS PAGE ---
  const SyllabusPage = () => (
    <div className="content-column">

      {/* STEP 1: SELECT BRANCH */}
      {!syllabusBranch && (
        <section>
          <h3 className="section-title"><FaBookOpen /> Syllabus: Select Department</h3>
          <div className="dept-grid">
            {departments.map((dept) => (
              <motion.div 
                key={dept.id} whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}
                onClick={() => setSyllabusBranch(dept.id)}
                className={`dept-card ${dept.color}`}
              >
                <div className="dept-icon">{dept.icon}</div>
                <h4>{dept.name}</h4><p>Select Scheme âžœ</p>
              </motion.div>
            ))}
          </div>
        </section>
      )}

      {/* STEP 2: SELECT SCHEME */}
      {syllabusBranch && !syllabusScheme && (
        <section>
          <button onClick={() => setSyllabusBranch(null)} style={{marginBottom:"20px", display:"flex", alignItems:"center", gap:"10px", background:"none", border:"none", cursor:"pointer", color:"#666", fontWeight:"bold"}}>
            <FaArrowLeft /> Back to Departments
          </button>
          <h3 className="section-title">Select Scheme for {syllabusBranch}</h3>
          <div className="dept-grid">
            {schemes.map((scheme) => (
              <motion.div 
                key={scheme} whileHover={{ scale: 1.05 }}
                onClick={() => setSyllabusScheme(scheme)}
                className="dept-card"
                style={{borderLeft: "5px solid #2563eb", padding: "30px"}}
              >
                <div className="dept-icon"><FaCalendarAlt /></div>
                <h4>{scheme}</h4><p>View PDF âžœ</p>
              </motion.div>
            ))}
          </div>
        </section>
      )}

      {/* STEP 3: DOWNLOAD LINK */}
      {syllabusBranch && syllabusScheme && (
        <section>
           <button onClick={() => setSyllabusScheme(null)} style={{marginBottom:"20px", display:"flex", alignItems:"center", gap:"10px", background:"none", border:"none", cursor:"pointer", color:"#666", fontWeight:"bold"}}>
            <FaArrowLeft /> Back to Schemes
          </button>
          <h3 className="section-title">Download Syllabus</h3>

          <div className="upload-card" style={{border: "2px solid #2563eb"}}>
            <div className="upload-info">
              <span className="code-badge" style={{background:"#2563eb", color:"white"}}>PDF</span>
              <div>
                <h4 style={{fontSize: "1.2rem"}}>{syllabusBranch} - {syllabusScheme}</h4>
                <small>Official VTU Copy</small>
              </div>
            </div>
            {/* THIS LINK REDIRECTS TO VTU WEBSITE. YOU CAN REPLACE WITH YOUR OWN DRIVE LINKS LATER */}
            <button 
              className="btn-download" 
