import React, { useState, useEffect } from 'react';
import Admin from './Admin';
import { motion } from 'framer-motion';
import { 
  FaUniversity, FaSearch, FaWhatsapp, FaTelegram, FaBars, FaTimes, 
  FaLaptopCode, FaMicrochip, FaCogs, FaBuilding, FaFlask, FaCalculator, FaFileAlt, FaBell 
} from 'react-icons/fa';

function App() {
  const [currentBranch, setCurrentBranch] = useState("ALL");
  const [subjects, setSubjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isAdmin, setIsAdmin] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  // Fake "News" Data for the Updates Section
  const updates = [
    { title: "Extension of last date for internship selection", date: "Dec 10, 2025" },
    { title: "Revised Time Table for 7th Sem Exams", date: "Dec 11, 2025" },
    { title: "Submission of Online Application for Revaluation", date: "Dec 12, 2025" },
    { title: "Tentative Academic Calendar 2025-26", date: "Dec 14, 2025" }
  ];

  // Fetch Data
  useEffect(() => {
    fetch('http://localhost:5000/api/subjects')
      .then(res => res.json())
      .then(data => {
        setSubjects(data);
        setLoading(false);
      })
      .catch(err => console.error("Error:", err));
  }, []);

  // Department Categories (Mimicking VTU Circle Home Page)
  const departments = [
    { id: "CSE", name: "CSE-ISE", icon: <FaLaptopCode size={30} />, desc: "Notes, QP & Lab Manuals for CS/IS Streams" },
    { id: "ECE", name: "Electronics (ECE)", icon: <FaMicrochip size={30} />, desc: "Resources for ECE & TE Branches" },
    { id: "MECH", name: "Mechanical", icon: <FaCogs size={30} />, desc: "Study Material for Mech & Auto" },
    { id: "CIVIL", name: "Civil Engg", icon: <FaBuilding size={30} />, desc: "Notes & Models for Civil Stream" },
    { id: "FY", name: "First Year", icon: <FaFlask size={30} />, desc: "Physics & Chemistry Cycle (2025 Scheme)" },
  ];

  // Filter Data
  const filteredSubjects = currentBranch === "ALL" 
    ? subjects 
    : subjects.filter(sub => sub.branch === currentBranch || sub.branch === "CSE"); // Simple filter logic

  return (
    <div className="min-h-screen bg-[#f8f9fa] text-gray-800 font-sans">

      {/* --- NAVBAR (White & Clean) --- */}
      <nav className="bg-white shadow-sm sticky top-0 z-50 border-b border-gray-100">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-20 items-center">
            {/* Logo */}
            <div className="flex items-center gap-2 cursor-pointer" onClick={() => setCurrentBranch("ALL")}>
              <div className="bg-blue-600 text-white p-2 rounded-lg">
                <FaUniversity size={24} />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-900 tracking-tight">Mandeep's Guide</h1>
                <p className="text-xs text-blue-600 font-semibold tracking-wide uppercase">VTU Resource Hub</p>
              </div>
            </div>

            {/* Desktop Menu */}
            <div className="hidden md:flex items-center space-x-8">
              <span className="text-gray-600 hover:text-blue-600 font-medium cursor-pointer">Home</span>
              <span className="text-gray-600 hover:text-blue-600 font-medium cursor-pointer">Notes</span>
              <span className="text-gray-600 hover:text-blue-600 font-medium cursor-pointer">Syllabus</span>
              <span className="text-gray-600 hover:text-blue-600 font-medium cursor-pointer">Results</span>
              <button onClick={() => setIsAdmin(!isAdmin)} className="text-sm bg-gray-100 px-4 py-2 rounded-full font-bold text-gray-700 hover:bg-gray-200">
                {isAdmin ? "Exit Admin" : "Admin"}
              </button>
              <FaSearch className="text-gray-400 hover:text-blue-600 cursor-pointer" />
            </div>

            {/* Mobile Menu Button */}
            <div className="md:hidden">
              <button onClick={() => setMobileMenuOpen(!mobileMenuOpen)} className="text-gray-600">
                {mobileMenuOpen ? <FaTimes size={24} /> : <FaBars size={24} />}
              </button>
            </div>
          </div>
        </div>
      </nav>

      {/* --- MAIN LAYOUT (2 Columns) --- */}
      <div className="max-w-7xl mx-auto px-4 py-8 grid grid-cols-1 lg:grid-cols-3 gap-8">

        {/* === LEFT COLUMN (Main Content - 70%) === */}
        <div className="lg:col-span-2 space-y-8">

          {isAdmin ? (
            <Admin />
          ) : (
            <>
              {/* 1. LATEST UPDATES BOX */}
              <div className="bg-white rounded-xl shadow-sm border border-gray-100 overflow-hidden">
                <div className="bg-blue-600 px-6 py-3 flex items-center justify-between">
                  <h2 className="text-white font-bold flex items-center gap-2">
                    <FaBell /> Latest VTU Circulars
                  </h2>
                  <span className="text-blue-100 text-xs bg-blue-700 px-2 py-1 rounded">Live</span>
                </div>
                <div className="divide-y divide-gray-100">
                  {updates.map((update, i) => (
                    <div key={i} className="p-4 hover:bg-gray-50 transition cursor-pointer flex gap-3">
                      <div className="text-blue-600 font-bold text-xs mt-1 min-w-[80px]">{update.date}</div>
                      <div className="text-sm text-gray-700 font-medium">{update.title}</div>
                    </div>
                  ))}
                </div>
              </div>

              {/* 2. DEPARTMENT CARDS (The Grid) */}
              <div>
                <h3 className="text-xl font-bold text-gray-800 mb-4 border-l-4 border-blue-600 pl-3">
                  Select Department
                </h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {departments.map((dept) => (
                    <motion.div 
                      key={dept.id}
                      whileHover={{ y: -2 }}
                      className="bg-white p-5 rounded-xl shadow-sm border border-gray-100 hover:shadow-md cursor-pointer transition flex items-start gap-4"
                      onClick={() => setCurrentBranch(dept.id)}
                    >
                      <div className="p-3 bg-blue-50 text-blue-600 rounded-lg">
                        {dept.icon}
                      </div>
                      <div>
                        <h4 className="font-bold text-lg text-gray-900">{dept.name}</h4>
                        <p className="text-sm text-gray-500 mt-1">{dept.desc}</p>
                      </div>
                    </motion.div>
                  ))}
                </div>
              </div>

              {/* 3. RECENT UPLOADS LIST */}
              <div>
                <h3 className="text-xl font-bold text-gray-800 mb-4 border-l-4 border-blue-600 pl-3">
                  Latest Notes Uploads
                </h3>
                {loading ? (
                  <p className="text-center text-gray-500">Loading resources...</p>
                ) : (
                  <div className="grid gap-4">
                    {filteredSubjects.slice(0, 5).map((sub) => (
                      <div key={sub._id} className="bg-white p-4 rounded-lg shadow-sm border border-gray-100 flex justify-between items-center group hover:border-blue-200 transition">
                        <div>
                          <span className="text-xs font-bold text-blue-600 bg-blue-50 px-2 py-1 rounded uppercase mr-2">{sub.branch}</span>
                          <span className="text-xs text-gray-400">Sem {sub.semester}</span>
                          <h4 className="font-bold text-gray-800 group-hover:text-blue-600 mt-1">{sub.subject}</h4>
                          <p className="text-xs text-gray-500">{sub.subjectCode}</p>
                        </div>
                        <a href={sub.link} target="_blank" rel="noreferrer" className="bg-gray-100 text-gray-600 px-4 py-2 rounded-lg text-sm font-bold hover:bg-blue-600 hover:text-white transition">
                          Download
                        </a>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </>
          )}
        </div>

        {/* === RIGHT COLUMN (Sidebar - 30%) === */}
        <div className="space-y-6">

          {/* Join Community Widget */}
          <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100">
            <h3 className="font-bold text-gray-800 mb-4">Join Our Community</h3>
            <div className="space-y-3">
              <button className="w-full flex items-center justify-center gap-2 bg-green-500 text-white py-3 rounded-lg font-bold hover:bg-green-600 transition shadow-sm">
                <FaWhatsapp size={20} /> Join WhatsApp Group
              </button>
              <button className="w-full flex items-center justify-center gap-2 bg-blue-500 text-white py-3 rounded-lg font-bold hover:bg-blue-600 transition shadow-sm">
                <FaTelegram size={20} /> Join Telegram Channel
              </button>
            </div>
            <p className="text-xs text-center text-gray-400 mt-3">Get instant updates on your phone</p>
          </div>

          {/* Quick Links Widget */}
          <div className="bg-white p-6 rounded-xl shadow-sm border border-gray-100">
            <h3 className="font-bold text-gray-800 mb-4 border-b pb-2">Quick Links</h3>
            <ul className="space-y-3 text-sm font-medium text-gray-600">
              <li className="flex items-center gap-2 hover:text-blue-600 cursor-pointer"><FaCalculator className="text-blue-400"/> SGPA Calculator</li>
              <li className="flex items-center gap-2 hover:text-blue-600 cursor-pointer"><FaCalculator className="text-blue-400"/> CGPA Calculator</li>
              <li className="flex items-center gap-2 hover:text-blue-600 cursor-pointer"><FaFileAlt className="text-blue-400"/> Model Question Papers</li>
              <li className="flex items-center gap-2 hover:text-blue-600 cursor-pointer"><FaFileAlt className="text-blue-400"/> Syllabus Copy</li>
            </ul>
          </div>

          {/* Author / About Widget */}
          <div className="bg-blue-50 p-6 rounded-xl border border-blue-100">
            <h3 className="font-bold text-blue-800 mb-2">About Mandeep's Guide</h3>
            <p className="text-sm text-blue-900 opacity-80 leading-relaxed">
              We provide the latest VTU notes, question papers, and updates for engineering students. Built with ❤️ by Mandeep.
            </p>
          </div>

        </div>

      </div>

      {/* --- FOOTER --- */}
      <footer className="bg-white border-t border-gray-200 mt-12 py-8">
        <div className="max-w-7xl mx-auto px-4 text-center text-gray-500 text-sm">
          <p className="font-medium">© 2025 Mandeep's Guide. All rights reserved.</p>
          <div className="flex justify-center gap-4 mt-2">
            <span className="hover:text-blue-600 cursor-pointer">Privacy Policy</span>
            <span className="hover:text-blue-600 cursor-pointer">Terms & Conditions</span>
            <span className="hover:text-blue-600 cursor-pointer">Contact Us</span>
          </div>
        </div>
      </footer>

    </div>
  );
}

export default App;
