import React, { useState, useEffect } from 'react';
import Admin from './Admin';
import { motion } from 'framer-motion';
import Marquee from "react-fast-marquee";
import { 
  FaUniversity, FaSearch, FaWhatsapp, FaTelegram, FaBars, FaTimes, 
  FaLaptopCode, FaMicrochip, FaCogs, FaBuilding, FaFlask, FaCalculator, FaFileAlt, FaBell, FaDownload, FaArrowRight
} from 'react-icons/fa';

function App() {
  const [currentBranch, setCurrentBranch] = useState("ALL");
  const [subjects, setSubjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isAdmin, setIsAdmin] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  // --- DATA (Keep exact same info) ---
  const updates = [
    "üì¢ Extension of last date for internship selection",
    "üìÖ Revised Time Table for 7th Sem Exams",
    "üìù Submission of Online Application for Revaluation",
    "üéì Tentative Academic Calendar 2025-26"
  ];

  useEffect(() => {
    fetch('http://localhost:5000/api/subjects')
      .then(res => res.json())
      .then(data => { setSubjects(data); setLoading(false); })
      .catch(err => console.error("Error:", err));
  }, []);

  const departments = [
    { id: "CSE", name: "CSE-ISE", icon: <FaLaptopCode />, color: "from-blue-500 to-cyan-400" },
    { id: "ECE", name: "Electronics", icon: <FaMicrochip />, color: "from-purple-500 to-indigo-400" },
    { id: "MECH", name: "Mechanical", icon: <FaCogs />, color: "from-orange-500 to-red-400" },
    { id: "CIVIL", name: "Civil Engg", icon: <FaBuilding />, color: "from-green-500 to-emerald-400" },
    { id: "FY", name: "First Year", icon: <FaFlask />, color: "from-pink-500 to-rose-400" },
  ];

  const filteredSubjects = currentBranch === "ALL" ? subjects : subjects.filter(sub => sub.branch === currentBranch || sub.branch === "CSE");

  return (
    <div className="min-h-screen bg-gray-50 font-sans selection:bg-blue-200">

      {/* --- 1. MODERN NAVBAR (Glass Effect) --- */}
      <nav className="bg-white/80 backdrop-blur-md sticky top-0 z-50 border-b border-gray-100 shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-20 items-center">
            {/* Logo */}
            <div className="flex items-center gap-3 cursor-pointer group" onClick={() => setCurrentBranch("ALL")}>
              <div className="bg-gradient-to-tr from-blue-600 to-indigo-600 text-white p-2.5 rounded-xl shadow-lg shadow-blue-200 group-hover:rotate-12 transition-transform">
                <FaUniversity size={22} />
              </div>
              <div>
                <h1 className="text-2xl font-extrabold text-transparent bg-clip-text bg-gradient-to-r from-gray-900 to-gray-700">Mandeep's Guide</h1>
              </div>
            </div>

            {/* Desktop Menu */}
            <div className="hidden md:flex items-center space-x-8 text-sm font-semibold text-gray-600">
              {['Home', 'Notes', 'Syllabus', 'Results'].map((item) => (
                <span key={item} className="hover:text-blue-600 cursor-pointer transition-colors relative group">
                  {item}
                  <span className="absolute bottom-[-4px] left-0 w-0 h-0.5 bg-blue-600 transition-all group-hover:w-full"></span>
                </span>
              ))}

              <button onClick={() => setIsAdmin(!isAdmin)} className="px-5 py-2 rounded-full bg-gray-900 text-white hover:bg-gray-800 transition shadow-lg hover:shadow-xl transform hover:-translate-y-0.5">
                {isAdmin ? "Exit Admin" : "Admin Panel"}
              </button>
            </div>

            {/* Mobile Toggle */}
            <button onClick={() => setMobileMenuOpen(!mobileMenuOpen)} className="md:hidden text-gray-600 p-2 rounded-lg hover:bg-gray-100">
              {mobileMenuOpen ? <FaTimes size={24} /> : <FaBars size={24} />}
            </button>
          </div>
        </div>
      </nav>

      {/* --- 2. HERO / TICKER SECTION --- */}
      <div className="bg-gradient-to-r from-blue-900 to-indigo-900 text-white py-3">
        <div className="max-w-7xl mx-auto flex items-center gap-4 px-4">
          <span className="bg-red-500 text-xs font-bold px-3 py-1 rounded-full animate-pulse whitespace-nowrap">LIVE UPDATES</span>
          <Marquee gradient={false} speed={50} className="text-sm font-medium opacity-90">
            {updates.map((u, i) => <span key={i} className="mx-8">‚Ä¢ {u}</span>)}
          </Marquee>
        </div>
      </div>

      {/* --- MAIN LAYOUT --- */}
      <div className="max-w-7xl mx-auto px-4 py-10 grid grid-cols-1 lg:grid-cols-12 gap-8">

        {/* === LEFT COLUMN (Content - 8 cols) === */}
        <div className="lg:col-span-8 space-y-10">

          {isAdmin ? (
             <motion.div initial={{opacity:0, y:20}} animate={{opacity:1, y:0}}><Admin /></motion.div>
          ) : (
            <>
              {/* FEATURE: Department Grid */}
              <section>
                <h3 className="text-xl font-bold text-gray-800 mb-6 flex items-center gap-2">
                  <span className="w-1.5 h-6 bg-blue-600 rounded-full"></span> Browse by Department
                </h3>
                <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                  {departments.map((dept, index) => (
                    <motion.div 
                      key={dept.id}
                      whileHover={{ scale: 1.03 }}
                      whileTap={{ scale: 0.98 }}
                      onClick={() => setCurrentBranch(dept.id)}
                      className={`relative overflow-hidden p-6 rounded-2xl cursor-pointer shadow-sm hover:shadow-lg transition-all bg-white group border border-gray-100`}
                    >
                      <div className={`absolute top-0 right-0 w-24 h-24 bg-gradient-to-br ${dept.color} opacity-10 rounded-bl-full -mr-4 -mt-4 transition-all group-hover:opacity-20`}></div>
                      <div className={`w-12 h-12 rounded-xl bg-gradient-to-br ${dept.color} flex items-center justify-center text-white text-xl mb-3 shadow-md`}>
                        {dept.icon}
                      </div>
                      <h4 className="font-bold text-gray-800">{dept.name}</h4>
                      <p className="text-xs text-gray-400 mt-1 font-medium">View Resources ‚ûú</p>
                    </motion.div>
                  ))}
                </div>
              </section>

              {/* FEATURE: Recent Uploads */}
              <section>
                <h3 className="text-xl font-bold text-gray-800 mb-6 flex items-center gap-2">
                  <span className="w-1.5 h-6 bg-green-500 rounded-full"></span> Latest Materials
                </h3>

                <div className="space-y-4">
                  {loading ? (
                    [1,2,3].map(i => <div key={i} className="h-20 bg-gray-200 rounded-xl animate-pulse"></div>)
                  ) : (
                    filteredSubjects.slice(0, 6).map((sub) => (
                      <motion.div 
                        key={sub._id}
                        initial={{ opacity: 0, x: -10 }}
                        whileInView={{ opacity: 1, x: 0 }}
                        viewport={{ once: true }}
                        className="group bg-white p-5 rounded-2xl border border-gray-100 shadow-sm hover:shadow-md hover:border-blue-100 transition-all flex justify-between items-center"
                      >
                        <div className="flex items-center gap-4">
                          <div className="bg-blue-50 text-blue-600 font-bold text-xs p-3 rounded-xl min-w-[60px] text-center">
                            {sub.subjectCode}
                          </div>
                          <div>
                            <h4 className="font-bold text-gray-800 group-hover:text-blue-600 transition-colors">{sub.subject}</h4>
                            <div className="flex gap-2 text-xs text-gray-400 mt-1">
                              <span className="bg-gray-100 px-2 py-0.5 rounded">{sub.branch}</span>
                              <span className="bg-gray-100 px-2 py-0.5 rounded">{sub.semester}th Sem</span>
                            </div>
                          </div>
                        </div>
                        <a 
                          href={sub.link} 
                          target="_blank" 
                          rel="noreferrer"
                          className="w-10 h-10 flex items-center justify-center rounded-full bg-gray-50 text-gray-600 group-hover:bg-blue-600 group-hover:text-white transition-all"
                        >
                          <FaDownload />
                        </a>
                      </motion.div>
                    ))
                  )}
                </div>
              </section>
            </>
          )}
        </div>

        {/* === RIGHT COLUMN (Sidebar - 4 cols) === */}
        <div className="lg:col-span-4 space-y-6">

          {/* Social Widget */}
          <div className="bg-gradient-to-br from-indigo-600 to-purple-700 rounded-2xl p-6 text-white shadow-xl relative overflow-hidden">
            <div className="absolute top-0 right-0 bg-white opacity-10 w-32 h-32 rounded-full -mr-10 -mt-10"></div>
            <h3 className="font-bold text-xl mb-2 relative z-10">Join the Community</h3>
            <p className="text-indigo-100 text-sm mb-6 relative z-10 opacity-90">Get instant exam alerts and notes directly on your phone.</p>

            <div className="space-y-3 relative z-10">
              <button className="w-full flex items-center justify-center gap-3 bg-white/20 backdrop-blur-sm hover:bg-white/30 border border-white/20 py-3 rounded-xl font-bold transition">
                <FaWhatsapp className="text-green-300 text-xl" /> WhatsApp Group
              </button>
              <button className="w-full flex items-center justify-center gap-3 bg-white/20 backdrop-blur-sm hover:bg-white/30 border border-white/20 py-3 rounded-xl font-bold transition">
                <FaTelegram className="text-blue-300 text-xl" /> Telegram Channel
              </button>
            </div>
          </div>

          {/* Tools Widget */}
          <div className="bg-white rounded-2xl shadow-sm border border-gray-100 p-6">
            <h3 className="font-bold text-gray-800 mb-4 border-b border-gray-100 pb-2">Student Tools</h3>
            <div className="grid grid-cols-2 gap-3">
              <div className="bg-gray-50 hover:bg-blue-50 hover:text-blue-600 p-4 rounded-xl cursor-pointer transition text-center group">
                <FaCalculator className="mx-auto mb-2 text-gray-400 group-hover:text-blue-500" />
                <span className="text-xs font-bold">SGPA Calc</span>
              </div>
              <div className="bg-gray-50 hover:bg-purple-50 hover:text-purple-600 p-4 rounded-xl cursor-pointer transition text-center group">
                <FaFileAlt className="mx-auto mb-2 text-gray-400 group-hover:text-purple-500" />
                <span className="text-xs font-bold">Model QP</span>
              </div>
            </div>
          </div>

          {/* About Widget */}
          <div className="bg-blue-50 rounded-2xl p-6 border border-blue-100">
            <h4 className="font-bold text-blue-800 mb-2 flex items-center gap-2">
              <FaUniversity /> About Us
            </h4>
            <p className="text-sm text-blue-900/80 leading-relaxed">
              Mandeep's Guide is a student-first initiative to simplify VTU resources. We are open source and community driven.
            </p>
          </div>

        </div>
      </div>

      {/* --- FOOTER --- */}
      <footer className="bg-white border-t border-gray-200 mt-20">
        <div className="max-w-7xl mx-auto px-4 py-12 text-center">
          <div className="flex justify-center gap-6 mb-8">
            {['Home', 'About', 'Contact', 'Privacy'].map(link => (
              <a href="#" key={link} className="text-gray-500 hover:text-blue-600 font-medium text-sm transition">{link}</a>
            ))}
          </div>
          <p className="text-gray-400 text-sm">¬© 2025 Mandeep's Guide. Designed with ‚ù§Ô∏è in India.</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
