import React, { useState } from 'react';
import './App.css';
import resourceData from './data/resources.json'; // Importing your "Database"

function App() {
  // This variable holds the semester the user wants to see (Default is 7)
  const [currentSem, setCurrentSem] = useState(7);

  // This filters the data to show ONLY the selected semester
  const filteredSubjects = resourceData.filter(item => item.semester === currentSem);

  return (
    <div className="App" style={{ fontFamily: 'Arial, sans-serif' }}>

      {/* 1. Header Section */}
      <header style={{ backgroundColor: "#282c34", color: "white", padding: "20px", textAlign: "center" }}>
        <h1>VTU Developer Hub</h1>
        <p>Your one-stop destination for notes and QP</p>
      </header>

      {/* 2. Semester Buttons */}
      <div style={{ display: 'flex', justifyContent: 'center', margin: '20px', gap: '10px' }}>
        {[5, 6, 7, 8].map(sem => (
          <button 
            key={sem}
            onClick={() => setCurrentSem(sem)}
            style={{
              padding: '10px 20px',
              backgroundColor: currentSem === sem ? '#007bff' : '#ddd', // Blue if selected, Gray if not
              color: currentSem === sem ? 'white' : 'black',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              fontWeight: 'bold'
            }}
          >
            {sem}th Sem
          </button>
        ))}
      </div>

      {/* 3. The List of Subjects */}
      <main style={{ maxWidth: "800px", margin: "0 auto", padding: "20px" }}>
        {filteredSubjects.length === 0 ? (
          <p style={{textAlign: "center"}}>No resources found for this semester yet.</p>
        ) : (
          filteredSubjects.map((sub) => (
            <div key={sub.id} style={{ border: "1px solid #ddd", borderRadius: "8px", padding: "20px", marginBottom: "20px", boxShadow: "0 2px 5px rgba(0,0,0,0.1)" }}>
              <h2 style={{ margin: "0 0 10px 0", color: "#333" }}>{sub.subject}</h2>
              <span style={{ backgroundColor: "#eee", padding: "5px 10px", borderRadius: "15px", fontSize: "0.8em", color: "#555" }}>
                {sub.subjectCode}
              </span>

              <div style={{ marginTop: "15px" }}>
                <h4 style={{ margin: "0 0 5px 0" }}>Downloads:</h4>
                {sub.modules.map((mod, index) => (
                  <a 
                    key={index} 
                    href={mod.link} 
                    target="_blank" 
                    rel="noreferrer"
                    style={{ display: "block", color: "#007bff", textDecoration: "none", margin: "5px 0" }}
                  >
                    ðŸ“„ {mod.moduleName}
                  </a>
                ))}
              </div>
            </div>
          ))
        )}
      </main>

    </div>
  );
}

export default App;
