import React, { useState } from 'react';
import { FaUserShield, FaPlus, FaSignOutAlt, FaLock } from 'react-icons/fa';

function Admin() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState("");
  
  // Login Form State
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loginError, setLoginError] = useState("");
  
  // Upload Form State
  const [formData, setFormData] = useState({ 
    branch: 'CSE', semester: 7, subject: '', subjectCode: '', link: '' 
  });
  
  // Add New Admin State
  const [newAdmin, setNewAdmin] = useState({ newUsername: "", newPassword: "" });
  const [message, setMessage] = useState("");

  // --- 1. HANDLE LOGIN ---
  const handleLogin = async (e) => {
    e.preventDefault();
    setLoginError("");
    
    try {
      const res = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password })
      });
      const data = await res.json();
      
      if (data.success) {
        setIsLoggedIn(true);
        setCurrentUser(data.username);
      } else {
        setLoginError("‚ùå " + data.message);
      }
    } catch (err) {
      setLoginError("‚ùå Server Error. Is Backend running?");
    }
  };

  // --- 2. HANDLE UPLOAD ---
  const handleUpload = async (e) => {
    e.preventDefault();
    setMessage("Uploading...");
    try {
      const res = await fetch('http://localhost:5000/api/subjects', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      if (res.ok) {
        setMessage("‚úÖ Resource Uploaded Successfully!");
        setFormData({ ...formData, subject: '', subjectCode: '', link: '' });
      } else { setMessage("‚ùå Upload Failed"); }
    } catch (err) { setMessage("‚ùå Server Error"); }
  };

  // --- 3. HANDLE ADD NEW ADMIN ---
  const handleAddAdmin = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:5000/api/add-admin', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newAdmin)
      });
      const data = await res.json();
      if (data.success) {
        alert("‚úÖ New Admin Added!");
        setNewAdmin({ newUsername: "", newPassword: "" });
      } else {
        alert("‚ùå " + data.message);
      }
    } catch (err) { alert("‚ùå Error adding admin"); }
  };

  // === VIEW 1: LOGIN SCREEN ===
  if (!isLoggedIn) {
    return (
      <div style={{
        maxWidth: "400px", margin: "50px auto", padding: "40px", 
        background: "white", borderRadius: "15px", 
        boxShadow: "0 10px 25px rgba(0,0,0,0.1)", textAlign: "center",
        border: "1px solid #e5e7eb"
      }}>
        <div style={{background: "#eff6ff", width: "60px", height: "60px", borderRadius: "50%", display: "flex", alignItems: "center", justifyContent: "center", margin: "0 auto 20px"}}>
          <FaLock size={24} color="#2563eb"/>
        </div>
        <h2 style={{marginBottom: "10px", color: "#1f2937", fontSize: "1.5rem"}}>Admin Access</h2>
        <p style={{marginBottom: "30px", color: "#6b7280", fontSize: "0.9rem"}}>Enter credentials to manage resources</p>
        
        <form onSubmit={handleLogin} style={{display: "flex", flexDirection: "column", gap: "15px"}}>
          <input 
            type="text" placeholder="Username" 
            value={username} onChange={e=>setUsername(e.target.value)} 
            style={{padding: "12px", borderRadius: "8px", border: "1px solid #d1d5db", outline: "none"}} 
          />
          <input 
            type="password" placeholder="Password" 
            value={password} onChange={e=>setPassword(e.target.value)} 
            style={{padding: "12px", borderRadius: "8px", border: "1px solid #d1d5db", outline: "none"}} 
          />
          <button type="submit" style={{padding: "12px", background: "#2563eb", color: "white", border: "none", borderRadius: "8px", cursor: "pointer", fontWeight: "bold", fontSize: "1rem", marginTop: "10px"}}>
            Secure Login
          </button>
        </form>
        {loginError && <p style={{color: "#dc2626", marginTop: "20px", fontSize: "0.9rem", fontWeight: "600"}}>{loginError}</p>}
      </div>
    );
  }

  // === VIEW 2: DASHBOARD (Logged In) ===
  return (
    <div style={{maxWidth: "800px", margin: "0 auto", padding: "20px"}}>
      
      {/* HEADER */}
      <div style={{display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "30px", background: "white", padding: "15px 25px", borderRadius: "12px", boxShadow: "0 2px 5px rgba(0,0,0,0.05)"}}>
        <div style={{display: "flex", alignItems: "center", gap: "10px"}}>
          <div style={{width: "10px", height: "10px", background: "#22c55e", borderRadius: "50%"}}></div>
          <h3 style={{margin: 0, color: "#374151"}}>Welcome, {currentUser}</h3>
        </div>
        <button onClick={() => setIsLoggedIn(false)} style={{background: "#fee2e2", color: "#dc2626", border: "none", padding: "8px 16px", borderRadius: "8px", cursor: "pointer", fontWeight: "600", display: "flex", alignItems: "center", gap: "8px"}}>
          <FaSignOutAlt /> Logout
        </button>
      </div>

      {/* UPLOAD SECTION */}
      <div style={{background: "white", padding: "30px", borderRadius: "15px", boxShadow: "0 4px 6px rgba(0,0,0,0.02)", border: "1px solid #e5e7eb", marginBottom: "30px"}}>
        <h3 style={{marginBottom: "20px", borderBottom: "2px solid #e5e7eb", paddingBottom: "10px", color: "#111827"}}>üöÄ Upload New Resource</h3>
        <form onSubmit={handleUpload} style={{display: "flex", flexDirection: "column", gap: "15px"}}>
          <div style={{display: "grid", gridTemplateColumns: "1fr 1fr", gap: "15px"}}>
            <select name="branch" value={formData.branch} onChange={e=>setFormData({...formData, branch:e.target.value})} style={{padding: "12px", border: "1px solid #d1d5db", borderRadius: "8px"}}>
              <option value="CSE">CSE</option><option value="ECE">ECE</option><option value="MECH">MECH</option><option value="CIVIL">CIVIL</option><option value="FY">First Year</option>
            </select>
            <select name="semester" value={formData.semester} onChange={e=>setFormData({...formData, semester:e.target.value})} style={{padding: "12px", border: "1px solid #d1d5db", borderRadius: "8px"}}>
              {[1,2,3,4,5,6,7,8].map(s=><option key={s} value={s}>{s}th Sem</option>)}
            </select>
          </div>
          <input type="text" placeholder="Subject Name (e.g. Cloud Computing)" value={formData.subject} onChange={e=>setFormData({...formData, subject:e.target.value})} required style={{padding: "12px", border: "1px solid #d1d5db", borderRadius: "8px"}} />
          <input type="text" placeholder="Subject Code (e.g. 18CS71)" value={formData.subjectCode} onChange={e=>setFormData({...formData, subjectCode:e.target.value})} required style={{padding: "12px", border: "1px solid #d1d5db", borderRadius: "8px"}} />
          <input type="url" placeholder="Google Drive / PDF Link" value={formData.link} onChange={e=>setFormData({...formData, link:e.target.value})} required style={{padding: "12px", border: "1px solid #d1d5db", borderRadius: "8px"}} />
          <button type="submit" style={{padding: "12px", background: "#16a34a", color: "white", border: "none", borderRadius: "8px", cursor: "pointer", fontWeight: "bold", fontSize: "1rem"}}>Upload to Database</button>
        </form>
        {message && <p style={{marginTop: "15px", fontWeight: "bold", color: message.includes("Error") || message.includes("Failed") ? "red" : "green"}}>{message}</p>}
      </div>

      {/* ADD NEW ADMIN SECTION */}
      <div style={{background: "#eff6ff", padding: "25px", borderRadius: "15px", border: "1px dashed #3b82f6"}}>
        <h3 style={{marginBottom: "15px", color: "#1e40af", display: "flex", alignItems: "center", gap: "10px"}}>
          <FaUserShield /> Add New Admin (Limit: 5)
        </h3>
        <form onSubmit={handleAddAdmin} style={{display: "flex", gap: "10px", flexWrap: "wrap"}}>
          <input type="text" placeholder="New Username" value={newAdmin.newUsername} onChange={e=>setNewAdmin({...newAdmin, newUsername:e.target.value})} style={{flex: 1, padding: "10px", borderRadius: "8px", border: "1px solid #bfdbfe", minWidth: "200px"}} />
          <input type="password" placeholder="New Password" value={newAdmin.newPassword} onChange={e=>setNewAdmin({...newAdmin, newPassword:e.target.value})} style={{flex: 1, padding: "10px", borderRadius: "8px", border: "1px solid #bfdbfe", minWidth: "200px"}} />
          <button type="submit" style={{background: "#2563eb", color: "white", border: "none", padding: "10px 20px", borderRadius: "8px", cursor: "pointer", fontWeight: "bold", display: "flex", alignItems: "center", gap: "5px"}}>
            <FaPlus /> Add
          </button>
        </form>
      </div>
    </div>
  );
}

export default Admin;
