import React, { useState, useEffect } from 'react';
import './App.css';
import Admin from './Admin'; // Import the new Admin page

function App() {
  const [currentSem, setCurrentSem] = useState(7);
  const [subjects, setSubjects] = useState([]);
  const [loading, setLoading] = useState(true);

  // New State: Toggle between Student View and Admin View
  const [isAdmin, setIsAdmin] = useState(false);

  // Fetch Data Function (Moved outside so we can refresh it)
  const fetchSubjects = () => {
    fetch('http://localhost:5000/api/subjects')
      .then(response => response.json())
      .then(data => {
        setSubjects(data);
        setLoading(false);
      })
      .catch(error => console.error("Error fetching data:", error));
  };

  useEffect(() => {
    fetchSubjects();
  }, []);

  const filteredSubjects = subjects.filter(item => item.semester == currentSem);

  return (
    <div className="App" style={{ padding: "20px", fontFamily: "Arial" }}>

      {/* Header with Toggle Button */}
      <header style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "20px" }}>
        <h1>VTU Resource Hub</h1>
        <button 
          onClick={() => setIsAdmin(!isAdmin)}
          style={{ padding: "8px 15px", backgroundColor: isAdmin ? "#dc3545" : "#333", color: "white", border: "none", cursor: "pointer" }}
        >
          {isAdmin ? "Close Admin Panel" : "Go to Admin Panel"}
        </button>
      </header>

      {/* CONDITIONAL RENDERING: Show Admin or Student View */}
      {isAdmin ? (
        <Admin />
      ) : (
        <>
          {/* Semester Buttons */}
          <div style={{ marginBottom: '20px' }}>
            {[3, 4, 5, 6, 7, 8].map(sem => (
              <button 
                key={sem} 
                onClick={() => setCurrentSem(sem)}
                style={{ marginRight: '10px', padding: '10px', backgroundColor: currentSem === sem ? '#007bff' : '#ddd', color: currentSem === sem ? 'white' : 'black', border: "none" }}
              >
                {sem}th Sem
              </button>
            ))}
          </div>

          {/* List */}
          {loading ? <p>Loading resources...</p> : (
            filteredSubjects.length === 0 ? <p>No resources found. (Go to Admin Panel to add some!)</p> : 
            filteredSubjects.map(sub => (
              <div key={sub._id} style={{ border: "1px solid #ddd", padding: "15px", margin: "10px 0", borderRadius: "5px", boxShadow: "2px 2px 5px rgba(0,0,0,0.1)" }}>
                <h3 style={{margin: "0 0 5px 0"}}>{sub.subject}</h3>
                <span style={{ fontSize: "12px", background: "#eee", padding: "2px 8px", borderRadius: "10px" }}>{sub.subjectCode}</span>
                <br/>
                <a href={sub.link} target="_blank" rel="noreferrer" style={{ display: "inline-block", marginTop: "10px", color: "#007bff", textDecoration: "none", fontWeight: "bold" }}>
                  Download Notes â¬‡
                </a>
              </div>
            ))
          )}
        </>
      )}
    </div>
  );
}

export default App;
