import React, { useState, useEffect } from 'react';
import Admin from './Admin';
import { motion } from 'framer-motion';
import Marquee from "react-fast-marquee";
import { 
  FaUniversity, FaWhatsapp, FaTelegram, FaBars, FaTimes, 
  FaLaptopCode, FaMicrochip, FaCogs, FaBuilding, FaFlask, 
  FaFileAlt, FaDownload, FaBell, FaTrophy, FaBookOpen, 
  FaArrowLeft, FaLayerGroup, FaPaperPlane, FaCommentDots, FaCalendarAlt
} from 'react-icons/fa';
import './App.css';

function App() {
  const [subjects, setSubjects] = useState([]);
  const [syllabusList, setSyllabusList] = useState([]); // Store Syllabus Data
  const [loading, setLoading] = useState(true);

  // STATE MANAGEMENT
  const [isAdmin, setIsAdmin] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activePage, setActivePage] = useState("home"); 

  const [currentBranch, setCurrentBranch] = useState("ALL");
  const [currentSemester, setCurrentSemester] = useState(null);
  const [syllabusBranch, setSyllabusBranch] = useState(null);
  const [syllabusScheme, setSyllabusScheme] = useState(null);

  const [feedback, setFeedback] = useState({ name: "", message: "" });
  const [feedbackStatus, setFeedbackStatus] = useState("");

  const updates = [
    { text: "ðŸ“¢ Extension of last date for internship selection", link: "https://vtu.ac.in/en/internships" },
    { text: "ðŸ“… Revised Time Table for 7th Sem Exams", link: "https://vtu.ac.in/en/exam-timetable" },
    { text: "ðŸ“ Submission of Online Application for Revaluation", link: "https://results.vtu.ac.in" },
    { text: "ðŸŽ“ Tentative Academic Calendar 2025-26", link: "https://vtu.ac.in/en/academic-calendar" }
  ];

  // FETCH ALL DATA
  useEffect(() => {
    // 1. Fetch Notes
    fetch('http://localhost:5000/api/subjects')
      .then(res => res.json())
      .then(data => { setSubjects(data); setLoading(false); })
      .catch(err => console.error("Error Notes:", err));

    // 2. Fetch Syllabus (NEW)
    fetch('http://localhost:5000/api/syllabus')
      .then(res => res.json())
      .then(data => setSyllabusList(data))
      .catch(err => console.error("Error Syllabus:", err));
  }, []);

  const departments = [
    { id: "CSE", name: "CSE-ISE", icon: <FaLaptopCode />, color: "card-blue" },
    { id: "ECE", name: "Electronics", icon: <FaMicrochip />, color: "card-purple" },
    { id: "MECH", name: "Mechanical", icon: <FaCogs />, color: "card-orange" },
    { id: "CIVIL", name: "Civil Engg", icon: <FaBuilding />, color: "card-green" },
    { id: "FY", name: "First Year", icon: <FaFlask />, color: "card-pink" },
  ];

  const schemes = ["2022 Scheme", "2021 Scheme", "2018 Scheme", "2017 Scheme", "2015 Scheme"];

  const filteredSubjects = subjects.filter(sub => {
    if (currentBranch === "ALL") return true; 
    return sub.branch === currentBranch && sub.semester === currentSemester;
  });

  const handleNavClick = (page) => {
    setActivePage(page); setIsAdmin(false); setMobileMenuOpen(false);
    setCurrentBranch("ALL"); setCurrentSemester(null); setSyllabusBranch(null); setSyllabusScheme(null);
  };

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault(); setFeedbackStatus("Sending...");
    try {
      const res = await fetch('http://localhost:5000/api/feedback', {
        method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(feedback)
      });
      if (res.ok) { setFeedbackStatus("âœ… Sent!"); setFeedback({ name: "", message: "" }); setTimeout(() => setFeedbackStatus(""), 3000); }
    } catch (err) { setFeedbackStatus("âŒ Error."); }
  };

  // --- DYNAMIC SYLLABUS DOWNLOAD ---
  // Find the correct link from the database
  const currentSyllabusLink = syllabusList.find(
    s => s.branch === syllabusBranch && s.scheme === syllabusScheme
  )?.link;

  const SyllabusPage = () => (
    <div className="content-column">
      {/* STEP 1: DEPT */}
      {!syllabusBranch && (
        <section>
          <h3 className="section-title"><FaBookOpen /> Syllabus: Select Department</h3>
          <div className="dept-grid">
            {departments.map((dept) => (
              <motion.div key={dept.id} whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }} onClick={() => setSyllabusBranch(dept.id)} className={`dept-card ${dept.color}`}>
                <div className="dept-icon">{dept.icon}</div><h4>{dept.name}</h4><p>Select Scheme âžœ</p>
              </motion.div>
            ))}
          </div>
        </section>
      )}
      {/* STEP 2: SCHEME */}
      {syllabusBranch && !syllabusScheme && (
        <section>
          <button onClick={() => setSyllabusBranch(null)} style={{background:"none", border:"none", cursor:"pointer", fontWeight:"bold", marginBottom:"15px"}}>â¬… Back</button>
          <h3 className="section-title">Select Scheme for {syllabusBranch}</h3>
          <div className="dept-grid">
            {schemes.map((scheme) => (
              <motion.div key={scheme} whileHover={{ scale: 1.05 }} onClick={() => setSyllabusScheme(scheme)} className="dept-card" style={{borderLeft: "5px solid #2563eb", padding: "30px"}}>
                <div className="dept-icon"><FaCalendarAlt /></div><h4>{scheme}</h4><p>View PDF âžœ</p>
              </motion.div>
            ))}
          </div>
        </section>
      )}
      {/* STEP 3: DOWNLOAD */}
      {syllabusBranch && syllabusScheme && (
        <section>
           <button onClick={() => setSyllabusScheme(null)} style={{background:"none", border:"none", cursor:"pointer", fontWeight:"bold", marginBottom:"15px"}}>â¬… Back</button>
          <h3 className="section-title">Download Syllabus</h3>
          <div className="upload-card" style={{border: "2px solid #2563eb", flexDirection: "column", alignItems: "flex-start", gap: "15px"}}>
            <div className="upload-info">
              <span className="code-badge" style={{background:"#2563eb", color:"white"}}>PDF</span>
              <div><h4 style={{fontSize: "1.2rem"}}>{syllabusBranch} - {syllabusScheme}</h4><small>Official VTU Copy</small></div>
            </div>

            {/* LOGIC: IF LINK EXISTS IN DB, SHOW BUTTON. ELSE SHOW 'COMING SOON' */}
            {currentSyllabusLink ? (
              <button className="btn-download" style={{width: "100%", borderRadius: "8px", height: "50px", background: "#2563eb", color: "white", fontWeight: "bold"}} onClick={() => window.open(currentSyllabusLink, '_blank')}>
                <FaDownload /> Download Now
              </button>
            ) : (
              <div style={{background: "#f3f4f6", padding: "15px", width: "100%", borderRadius: "8px", textAlign: "center", color: "#666"}}>
                ðŸš« Not uploaded yet. Request via Feedback.
              </div>
            )}
          </div>
        </section>
      )}
    </div>
  );

  const ResultsPage = () => (
    <div className="content-column">
      <h3 className="section-title"><FaTrophy /> Check VTU Results</h3>
      <div className="dept-grid">
        <div className="dept-card card-blue" onClick={() => window.open('https://results.vtu.ac.in', '_blank')}><div className="dept-icon"><FaLaptopCode /></div><h4>7th & 8th Sem</h4></div>
        <div className="dept-card card-green" onClick={() => window.open('https://results.vtu.ac.in', '_blank')}><div className="dept-icon"><FaFlask /></div><h4>1st & 2nd Sem</h4></div>
      </div>
    </div>
  );

  return (
    <div className="app-container">
      <nav className="navbar">
        <div className="nav-content">
          <div className="logo-section" onClick={() => handleNavClick("home")}><div className="logo-icon"><FaUniversity /></div><h1>Mandeep's Guide</h1></div>
          <div className={`nav-links ${mobileMenuOpen ? "mobile-open" : ""}`}>
            <span onClick={() => handleNavClick("home")}>Home</span><span onClick={() => handleNavClick("syllabus")}>Syllabus</span><span onClick={() => handleNavClick("results")}>Results</span>
            <button onClick={() => setIsAdmin(!isAdmin)} className="btn-admin">{isAdmin ? "Exit" : "Admin"}</button>
          </div>
          <div className="menu-toggle" onClick={() => setMobileMenuOpen(!mobileMenuOpen)}>{mobileMenuOpen ? <FaTimes /> : <FaBars />}</div>
        </div>
      </nav>
      <div className="ticker-section"><span className="ticker-label">LIVE</span><Marquee gradient={false} speed={50} className="ticker-text">{updates.map((u, i) => <a key={i} href={u.link} target="_blank" rel="noreferrer" style={{color:"white", marginRight:"40px"}}>â€¢ {u.text}</a>)}</Marquee></div>

      <div className="main-grid">
        {isAdmin ? <div className="content-column"><Admin /></div> : activePage === "syllabus" ? <SyllabusPage /> : activePage === "results" ? <ResultsPage /> : (
          <div className="content-column">
            {currentBranch === "ALL" && (
              <section>
                <h3 className="section-title">Departments</h3>
                <div className="dept-grid">{departments.map((dept) => (<motion.div key={dept.id} whileHover={{ scale: 1.05 }} onClick={() => { setCurrentBranch(dept.id); setCurrentSemester(null); }} className={`dept-card ${dept.color}`}><div className="dept-icon">{dept.icon}</div><h4>{dept.name}</h4></motion.div>))}</div>
              </section>
            )}
            {currentBranch !== "ALL" && currentSemester === null && (
              <section>
                <button onClick={() => setCurrentBranch("ALL")} style={{marginBottom:"20px", background:"none", border:"none", cursor:"pointer"}}>â¬… Back</button>
                <div className="dept-grid">{[1, 2, 3, 4, 5, 6, 7, 8].map((sem) => (<motion.div key={sem} whileHover={{ scale: 1.05 }} onClick={() => setCurrentSemester(sem)} className="dept-card"><h4>{sem}th Sem</h4></motion.div>))}</div>
              </section>
            )}
            {currentBranch !== "ALL" && currentSemester !== null && (
              <section className="uploads-section">
                <button onClick={() => setCurrentSemester(null)} style={{marginBottom:"20px", background:"none", border:"none", cursor:"pointer"}}>â¬… Back</button>
                <h3 className="section-title">{currentBranch} - {currentSemester}th Sem</h3>
                <div className="uploads-list">{loading ? <p>Loading...</p> : filteredSubjects.length === 0 ? <p>No notes found.</p> : filteredSubjects.map((sub) => (<div key={sub._id} className="upload-card"><div className="upload-info"><h4>{sub.subject}</h4></div><a href={sub.link} target="_blank" rel="noreferrer" className="btn-download"><FaDownload /></a></div>))}</div>
              </section>
            )}
          </div>
        )}
        <div className="sidebar-column">
          <div className="sidebar-widget profile-widget"><img src={process.env.PUBLIC_URL + '/profile.jpg'} onError={(e) => {e.target.onerror = null; e.target.src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png"}} alt="Mandeep" /><h3>Mandeep</h3><p className="profile-role">Software Engineer</p></div>
          <div className="sidebar-widget gradient-widget"><h3>Join Community</h3><button className="btn-social whatsapp" onClick={() => window.open('https://chat.whatsapp.com/YOUR_LINK', '_blank')}><FaWhatsapp /> WhatsApp</button></div>
          <div className="sidebar-widget"><h3>Feedback</h3><form onSubmit={handleFeedbackSubmit} style={{display:"flex", flexDirection:"column", gap:"10px"}}><input placeholder="Name" value={feedback.name} onChange={e=>setFeedback({...feedback, name:e.target.value})} style={{padding:"8px"}}/><textarea placeholder="Message" value={feedback.message} onChange={e=>setFeedback({...feedback, message:e.target.value})} style={{padding:"8px"}}/><button type="submit" style={{background:"#2563eb", color:"white", padding:"8px", border:"none"}}>Send</button></form>{feedbackStatus && <p>{feedbackStatus}</p>}</div>
        </div>
      </div>
      <footer className="footer"><p>Â© 2025 Mandeep's Guide</p></footer>
    </div>
  );
}

export default App;
